INSERT INTO `car` (`id`, `todo`, `title`, `description`, `keyword`, `img`, `noiDung`) VALUES
(2, 'Thành Phố Huế - Cảng Sa kỳ', 'Thuê Xe Đà Nẵng - Thuê Xe Quảng Ngãi', 'Thuê xe ô tô từ Huế  đến Cảng Sa Kỳ giá rẻ đoạn đường ước tính 211 km', 'thuê xe đà nẵng , thuê xe quảng ngãi , thuê xe lý sơn', 'upload\\abcd_4.jpg', '<p><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/travel/saky.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<h3><span style=\"color:#000000\"><strong>Thu&ecirc; xe th&agrave;nh phố Huế&nbsp; đi cảng sa kỳ</strong></span></h3>\r\n\r\n<p><span style=\"color:#000000\">Đối với nhiều du kh&aacute;ch, để di chuyển đến đảo L&yacute; Sơn rất tốn k&eacute;m v&agrave; bất tiện. Nhưng c&aacute;c bạn đừng lo lắng, đến với <strong>Lysonvn</strong>, ch&uacute;ng t&ocirc;i sẽ hướng dẫn c&aacute;ch di chuyển nhanh ch&oacute;ng đến đảo L&yacute; Sơn, v&agrave; lu&ocirc;n c&oacute; sẵn c&aacute;c dịch vụ khuyến m&atilde;i đi k&egrave;m. Đặt xe đi Cảng Sa Kỳ - L&yacute; Sơn online gi&aacute; rẻ nhất</span></p>\r\n\r\n<p><span style=\"color:#000000\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/car/lc3bd-son.jpg\" style=\"height:99%; width:99%\" /></span></p>\r\n\r\n<h3><span style=\"color:#000000\"><strong>Bản gi&aacute; thu&ecirc; xe &ocirc; t&ocirc; th&agrave;nh phố Huế&nbsp;đi cảng sa kỳ gi&aacute; rẻ&nbsp;</strong></span></h3>\r\n\r\n<p><span style=\"color:#000000\">Quảng đường từ th&agrave;nh phố Huế đến cảng Sa Kỳ- L&yacute; Sơn d&agrave;i 220km, mất khoảng hơn 4h đồng hồ để đi xe. Để di chuyển từ Huế&nbsp;đến Cảng Sa Kỳ, qu&yacute; kh&aacute;ch c&oacute; thể sử dụng dịch vụ đặt xe gi&aacute; rẻ tại&nbsp;<strong>Lysonvn</strong>&nbsp;để được hưởng gi&aacute; ưu đ&atilde;i v&agrave; rẻ nhất. Tại đ&acirc;y ch&uacute;ng t&ocirc;i lu&ocirc;n c&oacute; sẵn xe di chuyển từ Huế đến Cảng Sa Kỳ - L&yacute; Sơn v&agrave; từ cảng Sa Kỳ đến th&agrave;nh phố Huế&nbsp;để phục vụ qu&yacute; kh&aacute;ch v&agrave;o mọi thời điểm, v&agrave; gi&aacute; rẻ nhất tr&ecirc;n thị trường</span></p>\r\n\r\n<p><span style=\"color:#000000\">Khi lựa chọn di chuyển từ th&agrave;nh phố Huế&nbsp;đến Cảng Sa Kỳ - L&yacute; Sơn,&nbsp;<strong>Lysonvn</strong>&nbsp;lu&ocirc;n l&agrave; một sự lựa chọn đ&aacute;ng tin cậy v&agrave; l&agrave; c&ocirc;ng ty duy nhất đảm bảo:</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"color:#000000\"><strong>Đ&uacute;ng giờ t&agrave;u chạy</strong></span></li>\r\n	<li><span style=\"color:#000000\"><strong>Xe mới, sạch sẽ</strong></span></li>\r\n	<li><span style=\"color:#000000\"><strong>C&oacute; nhiều loại xe 4-7-16-32 chỗ</strong></span></li>\r\n	<li><span style=\"color:#000000\"><strong>Xe chạy an to&agrave;n, đ&uacute;ng giờ</strong></span></li>\r\n	<li><span style=\"color:#000000\"><strong>Đưa đ&oacute;n tận nơi theo y&ecirc;u cầu</strong></span></li>\r\n	<li><span style=\"color:#000000\"><strong>T&agrave;i xế th&acirc;n thiện, c&oacute; tổng đ&agrave;i ph&agrave;n n&agrave;n cho qu&yacute; kh&aacute;ch</strong></span></li>\r\n	<li><span style=\"color:#000000\"><strong>Hỗ trợ đặt v&eacute; t&agrave;u miễn ph&iacute;, đến cảng Sa Kỳ chỉ cần l&ecirc;n t&agrave;u</strong></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\">Điểm đi&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Th&agrave;nh phố Huế</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\">Điểm đến&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Cảng Sa Kỳ</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\">Khoảng c&aacute;ch&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;211km</span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\">Thời gian&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 4h</span></p>\r\n	</li>\r\n</ul>\r\n\r\n<h3><span style=\"color:#000000\"><strong>Bảng gi&aacute; xe đi từ Sa Kỳ đến Đảo L&yacute; Sơn</strong></span></h3>\r\n\r\n<p><span style=\"color:#000000\">Đ&acirc;y l&agrave; bảng gi&aacute; xe rẻ nhất hiện nay của c&ocirc;ng ty du lịch Ho&agrave;ng Ph&uacute;c,&nbsp;<strong>gi&aacute; tr&ecirc;n đ&atilde; bao gồm TVA v&agrave; đưa đ&oacute;n tận nơi theo y&ecirc;u cầu</strong>. Ch&uacute;ng t&ocirc;i lu&ocirc;n c&oacute;<strong>&nbsp;tổng đ&agrave;i điện thoại 0944.22.44.55 v&agrave; chat Online miễn ph&iacute;</strong>&nbsp;để phục vụ qu&yacute; kh&aacute;ch</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellspacing=\"0\" class=\"table table-striped\" style=\"-webkit-text-stroke-width:0px; background-color:#ffffff; border-collapse:collapse; border:1px solid #dee2e6; box-sizing:border-box; color:#000000; font-family:Arial,sans-serif; font-size:16px; font-style:normal; font-variant-caps:normal; font-variant-ligatures:normal; font-weight:400; letter-spacing:normal; margin-bottom:16px; orphans:2; text-align:left; text-decoration-color:initial; text-decoration-style:initial; text-indent:0px; text-transform:none; white-space:normal; widows:2; width:730px; word-spacing:0px\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\" style=\"text-align:inherit; vertical-align:bottom\">#</th>\r\n			<th scope=\"col\" style=\"text-align:inherit; vertical-align:bottom\">Loại xe</th>\r\n			<th scope=\"col\" style=\"text-align:inherit; vertical-align:bottom\">Số chỗ</th>\r\n			<th scope=\"col\" style=\"text-align:inherit; vertical-align:bottom\">Gi&aacute; tiền</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<th scope=\"row\" style=\"text-align:inherit; vertical-align:top\">1</th>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">Đi k&eacute; xe</td>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">1 Chỗ</td>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">Li&ecirc;n hệ&nbsp;<span style=\"font-size:0.8rem\"><span style=\"color:#ffffff\"><span style=\"background-color:#eb2771\">HOT!</span></span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\" style=\"text-align:inherit; vertical-align:top\">2</th>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">Thu&ecirc; xe</td>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">4 Chỗ</td>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">850.000 VND&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\" style=\"text-align:inherit; vertical-align:top\">3</th>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">Thu&ecirc; xe</td>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">7 Chỗ</td>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">1.050.000 VND</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\" style=\"text-align:inherit; vertical-align:top\">4</th>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">Thu&ecirc; xe</td>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">16 Chỗ</td>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">1.500.000 VND</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\" style=\"text-align:inherit; vertical-align:top\">5</th>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">Thu&ecirc; xe</td>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">30 Chỗ</td>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">2.900.000 VND</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\" style=\"text-align:inherit; vertical-align:top\">6</th>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">Thu&ecirc; xe</td>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">45 Chỗ</td>\r\n			<td style=\"background-color:#ffffff; vertical-align:top\">5.400.000 VND</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#000000\"><strong>Li&ecirc;n hệ ngay</strong></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><a class=\"genric-btn danger circle\" href=\"tel:0944224455\" style=\"box-sizing:border-box; color:#ffffff; text-decoration:none; background:#f44a40; transition:all 0.3s ease 0s; border-radius:20px; display:inline-block; outline:none; line-height:40px; padding:0px 30px; font-size:0.8em; text-align:center; font-weight:500; cursor:pointer; border:1px solid transparent\">Gọi hotline 0944224455</a></span></span></span></span></p>\r\n\r\n<div class=\"content\" style=\"-webkit-text-stroke-width:0px; text-align:left; text-indent:0px\">\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Tất c&aacute;c c&aacute;c xe của ch&uacute;ng t&ocirc;i, điều l&agrave; xe mới, phong ph&uacute; về loại, rất tiện &iacute;ch cho việc đi du lịch gia đ&igrave;nh, du lịch bạn b&egrave; v&agrave; du lịch theo đo&agrave;n. Ch&uacute;ng t&ocirc;i đ&atilde; c&oacute; kinh nghiệm l&acirc;u năm, trong du lịch l&yacute; sơn, n&ecirc;n sẽ ưu ti&ecirc;n đ&uacute;ng giờ v&agrave; di chuyển an to&agrave;n để cho qu&yacute; kh&aacute;ch c&oacute; 1 kỳ nghỉ tại L&yacute; Sơn tốt nhất, nếu sai giờ, ch&uacute;ng t&ocirc;i xin nhận ho&agrave;n to&agrave;n tr&aacute;ch nhiệm v&agrave; đền b&ugrave;.</span></span></span></span></p>\r\n\r\n<div class=\"row\" style=\"margin-left:-15px; margin-right:-15px\">\r\n<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue xe da nang di cang sa ky ly son\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/xe-du-lich-ly-son-1.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n<p style=\"text-align:center !important\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">D&agrave;n xe mới, phong ph&uacute; v&agrave; đa dạng</span></span></span></span></span></p>\r\n</div>\r\n\r\n<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue xe da nang di cang sa ky ly son\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/xe-du-lich-ly-son-2.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n<p style=\"text-align:center !important\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Nội thất sang trọng tiện nghi du lịch</span></span></span></span></span></p>\r\n</div>\r\n\r\n<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue xe da nang di cang sa ky ly son\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/xe-du-lich-ly-son-3.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n<p style=\"text-align:center !important\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">T&agrave;i xế chu đ&aacute;o, chia sẻ kinh nghiệm du lịch</span></span></span></span></span></p>\r\n</div>\r\n\r\n<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue xe da nang di cang sa ky ly son\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/xe-du-lich-ly-son-4.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n<p style=\"text-align:center !important\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Xe 4-7-16-36 chỗ phục vụ mọi kh&aacute;ch du lịch</span></span></span></span></span></p>\r\n\r\n<h3><strong>Hướng dẫn di chuyển đến đảo l&yacute; sơn</strong></h3>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"content\" style=\"-webkit-text-stroke-width:0px; text-align:left; text-indent:0px\">\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">L&yacute; Sơn l&agrave; một huyện đảo của tỉnh Quảng Ng&atilde;i, nằm c&aacute;ch Cảng Sa Kỳ khoảng 30km. V&igrave; thế để di chuyển đến đảo L&yacute; Sơn, c&aacute;c bạn cần phải di chuyển ra cảng Sa Kỳ rồi sau đ&oacute; bắt t&agrave;u Super biển đ&ocirc;ng để đi ra đảo L&yacute; Sơn</span></span></span></span></p>\r\n\r\n<p><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/travel/map.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p style=\"text-align:center !important\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Đảo L&yacute; Sơn c&aacute;ch đất liền 30km</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><strong>Hướng c&aacute;ch di chuyển tiết kiệm, nhanh ch&oacute;ng v&agrave; an to&agrave;n nhất</strong></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:18px\"><span style=\"color:#ff8800\">Bước 01:</span></span>&nbsp;Để di chuyển đến Cảng Sa Kỳ, ch&uacute;ng ta c&oacute; thể kết hợp, di chuyển bằng m&aacute;y bay hoặc t&agrave;u lửa đến c&aacute;c nh&agrave; ga, s&acirc;n bay gần đảo Cảng Sa Kỳ nhất như s&acirc;n bay Chu Lai, s&acirc;n bay Đ&agrave; Nẵng, ga Quảng Ng&atilde;i, hoặc th&agrave;nh phố Hội An, th&agrave;nh phố Đ&agrave; Nẵng v&agrave; Th&agrave;nh Phố Huế.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/car/TKT_VN1-1.jpg\" style=\"height:80%; width:99%\" /></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:18px\"><span style=\"color:#ff8800\">Bước 02:</span></span>&nbsp;Sau đ&oacute; c&aacute;c bạn thu&ecirc; 1 &ocirc;t&ocirc; kh&aacute;ch gi&aacute; rẻ để đi đến cảng Sa Kỳ, &ocirc;t&ocirc; kh&aacute;ch c&oacute; thể l&agrave; xe 4 chỗ, 7 chỗ hoặc 16 chỗ, t&ugrave;y số lượng người di chuyển. Nếu c&aacute;c bạn chỉ đi c&oacute; 1-2 người th&igrave; c&oacute; thể li&ecirc;n hệ với ch&uacute;ng t&ocirc;i để c&oacute; thể sắp xếp xe k&eacute;, m&agrave; kh&ocirc;ng cần phải thu&ecirc; nguy&ecirc;n xe. Hoặc c&aacute;c bạn c&oacute; thể tham khảo bảng gi&aacute;&nbsp;<strong>Thu&ecirc; xe &ocirc; t&ocirc; s&acirc;n bay Đ&agrave; Nẵng đến Cảng Sa Kỳ</strong>&nbsp;ở ph&iacute;a dưới của </span></span></span></span><span style=\"color:#000000\"><strong>Lysonvn</strong></span><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">. Hiện đang c&oacute;&nbsp;<strong>khuyến m&atilde;i giảm 20%</strong>&nbsp;gi&aacute; xe khi đặt chuyến về</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/car/thu-xe-du-lich-tuy-hoa-mercedes-sprinter-16-cho-1.jpg\" style=\"height:89%; width:99%\" /></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:18px\"><span style=\"color:#ff8800\">Bước 03:</span></span>&nbsp;Sau khi đến được cảng Sa Kỳ, c&aacute;c bạn phải mua v&eacute; t&agrave;u để di chuyển đến đảo L&yacute; Sơn. Nếu c&aacute;c bạn bị say t&agrave;u hoặc say s&oacute;ng, th&igrave; n&ecirc;n lựa chọn loại t&agrave;u lớn, để cho chuyến du lịch được trọn vẹn nhất</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/travel/tau-khach-super-bien-dong-dao-ly-son.jpg\" style=\"height:99%; width:99%\" /></span></span></span></span></p>\r\n\r\n<h3><strong>Mua v&eacute; t&agrave;u từ cảng sa kỳ đi đảo l&yacute; sơn</strong></h3>\r\n</div>\r\n\r\n<div class=\"content\" style=\"-webkit-text-stroke-width:0px; text-align:left; text-indent:0px\">\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">T&agrave;u Super Biển Đ&ocirc;ng l&agrave; con t&agrave;u hiện đại đ&oacute;ng mới ho&agrave;n to&agrave;n bằng vỏ hợp kim nh&ocirc;m &yacute;, lắp đặt 02 m&aacute;y tốc độ cao Yanmar (Nhật Bản) v&agrave; c&aacute;c thiết bị hiện đại, t&agrave;u gồm 139 ghế v&agrave; 01 ph&ograve;ng VIP, c&oacute; tốc độ tối đa 30 hải l&yacute;/giờ, tương đương 30&ndash;35 ph&uacute;t, khoảng c&aacute;ch đảo L&yacute; Sơn với đất liền sẽ được gần hơn, t&agrave;u Super Biển Đ&ocirc;ng phục vụ 3-6 chuyến/ng&agrave;y (t&ugrave;y theo c&aacute;c ng&agrave;y).</span></span></span></span></p>\r\n\r\n<h3><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:24px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"color:#002347\">Bảng giờ t&agrave;u đi từ cảng Sa Kỳ đến Đảo L&yacute; Sơn</span></span></span></span></span></span></span></h3>\r\n\r\n<table cellspacing=\"0\" class=\"table table-striped\" style=\"border-collapse:collapse; border:undefined; box-sizing:border-box; margin-bottom:16px; width:730px\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\" style=\"text-align:inherit; vertical-align:bottom\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">#</span></span></span></span></th>\r\n			<th scope=\"col\" style=\"text-align:inherit; vertical-align:bottom\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">T&ecirc;n t&agrave;u</span></span></span></span></th>\r\n			<th scope=\"col\" style=\"text-align:inherit; vertical