INSERT INTO `hotel` (`id`, `tenHotel`, `title`, `description`, `keyword`, `gia`, `img`, `diaChi`, `noiDung`) VALUES
(2, 'NHÀ NGHỈ BÌNH MINH', 'NHÀ NGHỈ BÌNH MINH - Nhà Nghỉ Lý Sơn', 'Lý Sơn là một huyện đảo thuộc tỉnh Quảng Ngãi, nơi đây không chỉ nổi tiếng bởi nghề trồng tỏi mà còn được thiên nhiên ban tặng cho một phong cảnh hữu tình mà khó nơi nào có được. Quý khách đến với Lý Sơn hãy chọn điểm dừng chân là Nhà nghỉ BÌNH MINH để tận hưởng trọn vẹn niềm vui với những giây phút thư giãn tuyệt vời, những kỷ niệm khó phai.', 'khách Sạn Lý Sơn ,Nhà Nghỉ Lý Sơn,NHÀ NGHỈ BÌNH MINH', 250000, 'upload/PlP3_full-bm.jpg', 'Lý Sơn', '<h2 style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"color:#6c6c6c\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:small\"><span style=\"color:#ff6600\"><strong>NH&Agrave; NGHỈ B&Igrave;NH MINH</strong></span></span></span></span></span></h2>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#000000\"><strong><span style=\"font-size:12px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">ĐC : Th&ocirc;n T&acirc;y &ndash; An Vĩnh &ndash; L&yacute; Sơn &ndash; Quảng Ng&atilde;i</span></span></strong></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"color:#6c6c6c\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>ĐT:&nbsp;<span style=\"font-size:small\"><span style=\"color:#ff6600\">0984.390.650 - 038.485.4942</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#000000\"><span style=\"font-size:12px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>--------------------------------- ***&nbsp;<strong>---------------------------------</strong></strong></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\">L&yacute; Sơn l&agrave; một huyện đảo thuộc tỉnh Quảng Ng&atilde;i, nơi đ&acirc;y kh&ocirc;ng chỉ nổi tiếng bởi nghề trồng tỏi m&agrave; c&ograve;n được thi&ecirc;n nhi&ecirc;n ban tặng cho một phong cảnh hữu t&igrave;nh m&agrave; kh&oacute; nơi n&agrave;o c&oacute; được. Qu&yacute; kh&aacute;ch đến với L&yacute; Sơn h&atilde;y chọn điểm dừng ch&acirc;n l&agrave;&nbsp;<strong>Nh&agrave; nghỉ B&Igrave;NH MINH</strong>&nbsp;để tận hưởng trọn vẹn niềm vui với những gi&acirc;y ph&uacute;t thư gi&atilde;n tuyệt vời, những kỷ niệm kh&oacute; phai.</span></p>\r\n\r\n<p><span style=\"color:#000000\"><strong>Nh&agrave; nghỉ B&Igrave;NH MINH</strong>, nằm cạnh di t&iacute;ch lịch sử cấp quốc gia&nbsp;<strong>&Acirc;m Linh Tự</strong>, ở khu vực&nbsp;<strong>trung t&acirc;m chợ hải sản</strong>, c&aacute;ch&nbsp;<strong>Cầu Cảng L&yacute; Sơn 50m</strong>, đ&acirc;y l&agrave; địa điểm nhộn nhịp nhất gần trung t&acirc;m mua sắm, ăn uống, vui chơi giải tr&iacute;.&nbsp;<br />\r\n&nbsp;<br />\r\n<strong>Nh&agrave; nghỉ B&Igrave;NH MINH</strong>&nbsp;l&agrave; điểm dừng ch&acirc;n nghỉ ngơi, thưởng thức c&aacute;c m&oacute;n ăn đặc sản hấp dẫn nhất của du kh&aacute;ch tr&ecirc;n mọi miền đất nước khi đến tham quan nghỉ dưỡng v&agrave; c&ocirc;ng t&aacute;c tại Đảo L&yacute; Sơn xinh đẹp n&agrave;y.&nbsp;</span></p>\r\n\r\n<p><span style=\"color:#000000\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/khachsan/full-bm.jpg\" style=\"height:99%; width:99%\" /></span></p>\r\n\r\n<p><span style=\"color:#000000\">Qu&yacute; kh&aacute;ch đến với&nbsp;<strong>Nh&agrave; nghỉ B&Igrave;NH MINH</strong>&nbsp;sẽ được chi&ecirc;m ngưỡng kh&ocirc;ng gian thật tuyệt vời, tho&aacute;ng m&aacute;t, sạch sẽ, ph&ograve;ng vệ sinh kh&eacute;p k&iacute;n, wifi... đồng thời Qu&yacute; kh&aacute;ch sẽ c&oacute; một kh&ocirc;ng gian ngắm cảnh thật tuyệt vời l&uacute;c buổi s&aacute;ng tinh mơ hay buổi ho&agrave;ng h&ocirc;n c&ugrave;ng h&ograve;a nguyện v&agrave;o từng cơn s&oacute;ng biển, Qu&yacute; kh&aacute;ch c&oacute; thể lắng nghe tiếng r&igrave; r&agrave;o v&agrave; ngắm nh&igrave;n những con t&agrave;u của ngư d&acirc;n đang lượn m&igrave;nh qua những cơn s&oacute;ng biết.&nbsp;<br />\r\n<br />\r\nĐến với&nbsp;<strong>Nh&agrave; nghỉ B&Igrave;NH MINH</strong>, Qu&yacute; kh&aacute;ch sẽ được đ&oacute;n tiếp nồng nhiệt, phục vụ chu đ&aacute;o, tận t&igrave;nh với th&aacute;i độ &acirc;n cần, niềm nở tạo cho du kh&aacute;ch đến đ&acirc;y cảm nhận được sự th&acirc;n thiện v&agrave; h&agrave;i l&ograve;ng. Chắc chắn rằng Qu&yacute; kh&aacute;ch sẽ c&oacute; cảm gi&aacute;c thoải m&aacute;i như quay về ch&iacute;nh ng&ocirc;i nh&agrave; của m&igrave;nh.<br />\r\n<strong>Gi&aacute; ph&ograve;ng c&oacute; m&aacute;y lạnh:</strong><br />\r\n<strong>+ Ph&ograve;ng đ&ocirc;i &nbsp;:&nbsp;350.000 đ/ng&agrave;y</strong><br />\r\n<strong>+ Ph&ograve;ng đơn :&nbsp;250.000 đ/ng&agrave;y</strong></span></p>\r\n\r\n<p><span style=\"color:#000000\"><strong>Nh&agrave; nghỉ mới x&acirc;y, Ph&ograve;ng ở tho&aacute;ng m&aacute;t, phục vụ chu đ&aacute;o, đầy đủ tiện nghi:</strong><br />\r\n- Ph&ograve;ng c&oacute; điều h&ograve;a, quạt điện, tivi, m&aacute;y n&ograve;ng lạnh.</span></p>\r\n\r\n<p><span style=\"color:#000000\">- Cho thu&ecirc; xe gắn m&aacute;y, xe du lịch.<br />\r\n- PREE WIFI tốc độ cao.<br />\r\n- Phục vụ ăn uống tận t&igrave;nh chu đ&aacute;o theo y&ecirc;u cầu của h&agrave;nh kh&aacute;ch<br />\r\n- Phục vụ nấu ăn cho kh&aacute;ch đặt theo đơn đặt h&agrave;ng phần ăn với những m&oacute;n ăn hải sản tại L&yacute; Sơn.</span></p>'),
(3, 'Nhà Nghỉ Quỳnh Anh', 'Nhà Nghỉ Quỳnh Anh -  Nhà Nghỉ Lý Sơn', 'Lý Sơn là một huyện đảo thuộc tỉnh Quảng Ngãi, nơi đây không chỉ nổi tiếng bởi nghề trồng tỏi mà còn được thiên nhiên ban tặng cho một phong cảnh hữu tình mà khó nơi nào có được. Quý khách đến với Lý Sơn hãy chọn điểm dừng chân là Nhà nghỉ QUỲNH ANH để tận hưởng trọn vẹn niềm vui với những giây phút thư giãn tuyệt vời, những kỷ niệm khó phai.', 'khách Sạn Lý Sơn ,Nhà Nghỉ Lý Sơn,Nhà Nghỉ Quỳnh Anh', 300000, 'upload/9woR_quynh-anh-1.png', 'Lý Sơn', '<p style=\"text-align:center\"><br />\r\n<span style=\"color:#c0392b\"><strong>NH&Agrave; NGHỈ QUỲNH ANH</strong></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#000000\"><strong>ĐC : Trung t&acirc;m Cầu Cảng &ndash; An Vĩnh &ndash; L&yacute; Sơn &ndash; Quảng Ng&atilde;i</strong></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#c0392b\"><strong>Li&ecirc;n hệ:&nbsp;0255.6279.868 - 0911.482.879 - 0399.353.868&nbsp;(Ms.Li&ecirc;n)</strong></span></p>\r\n\r\n<p style=\"text-align:center\"><strong>-------------------- o0o&nbsp;<strong>--------------------</strong></strong></p>\r\n\r\n<p><span style=\"color:#000000\"><strong>GI&Aacute; PH&Ograve;NG:</strong></span></p>\r\n\r\n<p><span style=\"color:#000000\"><strong>- Gi&aacute; ph&ograve;ng đ&ocirc;i l&agrave; :&nbsp;400.000 đồng/ ph&ograve;ng/ ng&agrave;y đ&ecirc;m</strong><br />\r\n<strong>- Gi&aacute; ph&ograve;ng đơn l&agrave; :&nbsp;250.000 đồng/ ph&ograve;ng/ ng&agrave;y đ&ecirc;m</strong></span></p>\r\n\r\n<p><span style=\"color:#000000\"><strong>- Một ph&ograve;ng cộng đồng (3 giường):&nbsp;Gi&aacute; li&ecirc;n hệ</strong></span></p>\r\n\r\n<p><span style=\"color:#000000\">L&yacute; Sơn l&agrave; một huyện đảo thuộc tỉnh Quảng Ng&atilde;i, nơi đ&acirc;y kh&ocirc;ng chỉ nổi tiếng bởi nghề trồng tỏi m&agrave; c&ograve;n được thi&ecirc;n nhi&ecirc;n ban tặng cho một phong cảnh hữu t&igrave;nh m&agrave; kh&oacute; nơi n&agrave;o c&oacute; được. Qu&yacute; kh&aacute;ch đến với L&yacute; Sơn h&atilde;y chọn điểm dừng ch&acirc;n l&agrave; Nh&agrave; nghỉ QUỲNH ANH&nbsp;để tận hưởng trọn vẹn niềm vui với những gi&acirc;y ph&uacute;t thư gi&atilde;n tuyệt vời, những kỷ niệm kh&oacute; phai.</span></p>\r\n\r\n<p><span style=\"color:#000000\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/khachsan/quynh-anh-1.png\" style=\"height:99%; width:99%\" /></span></p>\r\n\r\n<p><span style=\"color:#000000\">Nh&agrave; nghỉ QUỲNH ANH&nbsp;nằm ở khu vực Cầu Cảng L&yacute; Sơn, đ&acirc;y l&agrave; địa điểm nhộn nhịp nhất gần trung t&acirc;m mua sắm, ăn uống, vui chơi giải tr&iacute;.&nbsp;Được đưa v&agrave;o hoạt động từ đầu năm 2016, với dịch vụ tốt, gi&aacute; cả hợp l&yacute;&nbsp;Nh&agrave; nghỉ QUỲNH ANH&nbsp;l&agrave; điểm dừng ch&acirc;n nghỉ ngơi, thưởng thức c&aacute;c m&oacute;n ăn đặc sản hấp dẫn nhất của du kh&aacute;ch tr&ecirc;n mọi miền đất nước khi đến tham quan nghỉ dưỡng v&agrave; c&ocirc;ng t&aacute;c tại Đảo L&yacute; Sơn xinh đẹp n&agrave;y.<br />\r\n<br />\r\nĐến với&nbsp;Nh&agrave; nghỉ QUỲNH ANH, Qu&yacute; kh&aacute;ch sẽ được đ&oacute;n tiếp nồng nhiệt, phục vụ chu đ&aacute;o, tận t&igrave;nh với th&aacute;i độ &acirc;n cần, niềm nở tạo cho du kh&aacute;ch đến đ&acirc;y cảm nhận được sự th&acirc;n thiện v&agrave; h&agrave;i l&ograve;ng. Chắc chắn rằng Qu&yacute; kh&aacute;ch sẽ c&oacute; cảm gi&aacute;c thoải m&aacute;i như quay về ch&iacute;nh ng&ocirc;i nh&agrave; của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"color:#000000\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/khachsan/quynh-anh-2.png\" style=\"height:99%; width:99%\" /></span></p>\r\n\r\n<p><span style=\"color:#000000\">Ph&ograve;ng c&oacute; đầy đủ tiện nghi, ti vi phẳng, điều h&ograve;a, wifi, tolel kh&eacute;p k&iacute;n, sạch sẽ đem lại kh&ocirc;ng kh&iacute; tươi m&aacute;t sẽ l&agrave;m h&agrave;i l&ograve;ng qu&yacute; kh&aacute;ch khi một lần gh&eacute; đến v&agrave; nhất định lần sau qu&yacute; kh&aacute;ch sẽ kh&ocirc;ng qu&ecirc;n Nh&agrave; nghỉ QUỲNH ANH&nbsp;ch&uacute;ng t&ocirc;i, sự nhiệt t&igrave;nh phục vụ qu&yacute; kh&aacute;ch, th&acirc;n thiện m&agrave;&nbsp;Nh&agrave; nghỉ QUỲNH ANH&nbsp;của ch&uacute;ng t&ocirc;i sẽ mang lại.</span></p>\r\n\r\n<p><span style=\"color:#000000\">- Wifi miễn ph&iacute;<br />\r\n- Ph&ograve;ng rộng th&aacute;ng m&aacute;t, sạch, đẹp, y&ecirc;n tĩnh.<br />\r\n- Đầy đủ tiện nghi<br />\r\n- Phục vụ 24/24 giờ</span></p>\r\n\r\n<p><span style=\"color:#000000\">- Cho thu&ecirc; xe du lịch<br />\r\n- Cho thu&ecirc; xe gắn m&aacute;y.<br />\r\n- Phục vụ ăn uống theo y&ecirc;u cầu của qu&yacute; kh&aacute;ch ( đặc biệt l&agrave; hải sản L&yacute; Sơn ).&nbsp;</span></p>'),
(4, 'Nhà Nghỉ Lý Trí', 'khách Sạn Lý Sơn - Nhà Nghỉ Lý Sơn', 'Lý Sơn là điểm du lịch lý tưởng của Qúy khách, khi quý khách đến với Lý Sơn là điểm dừng chân nghỉ ngơi, dịch vụ nhà hàng, thì quý khách không thể nào bỏ lở Nhà nghỉ LÝ TRÍ.', 'khách Sạn Lý Sơn ,Nhà Nghỉ Lý Sơn,Nhà Nghỉ Lý Trí', 300000, 'upload/9UGa_nha-nghi-ly-tri2.jpg', 'Lý Sơn', '<h2 style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"color:#6c6c6c\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"color:#ff6600\"><strong>NH&Agrave; NGHỈ L&Yacute; TR&Iacute; &ndash; H&Acirc;N HẠNH ĐƯỢC Đ&Oacute;N TIẾP V&Agrave; PHỤC VỤ QU&Yacute; KH&Aacute;CH !&nbsp;</strong></span></span></span></span><span style=\"font-size:12px\"><span style=\"color:#6c6c6c\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;</span></span></span></h2>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#000000\"><span style=\"font-size:12px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mọi chi tiết vui long li&ecirc;n hệ</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#000000\"><span style=\"font-size:12px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>NH&Agrave; NGHỈ L&Yacute; TR&Iacute;</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#000000\"><span style=\"font-size:12px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Địa chỉ : Th&ocirc;n T&acirc;y &ndash; An Vĩnh &ndash; L&yacute; Sơn &ndash; Quảng Ng&atilde;i</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#d35400\"><span style=\"font-size:12px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>ĐT: 0553.507.117 - 096 129 2279 &ndash; 096 129 2239 (Mr. Tr&iacute;)</strong></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#d35400\"><span style=\"font-size:12px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Email: phantri.dosaco@gmail.com&nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\">L&yacute; Sơn l&agrave; điểm du lịch l&yacute; tưởng của Q&uacute;y kh&aacute;ch, khi qu&yacute; kh&aacute;ch đến với L&yacute; Sơn l&agrave; điểm dừng ch&acirc;n nghỉ ngơi, dịch vụ nh&agrave; h&agrave;ng, th&igrave; qu&yacute; kh&aacute;ch kh&ocirc;ng thể n&agrave;o bỏ lở Nh&agrave; nghỉ L&Yacute; TR&Iacute;. Quy m&ocirc; 12 ph&ograve;ng&nbsp;<strong>(Điều h&ograve;a, m&aacute;y nước n&oacute;ng lạnh)</strong>, nằm gần trung t&acirc;m cảng&nbsp; L&yacute; Sơn, gần địa điểm du lịch &Acirc;m Linh tự&nbsp;- nơi thờ c&uacute;ng những chiến sĩ Ho&agrave;ng Sa ( Th&ocirc;n T&acirc;y &ndash; An Vĩnh &ndash; L&yacute; Sơn).&nbsp;</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/khachsan/nha-nghi-ly-tri2.jpg\" style=\"height:99%; width:99%\" /><br />\r\nQ&uacute;y kh&aacute;ch đến Nh&agrave; nghỉ L&Yacute; TR&Iacute; sẽ được chi&ecirc;m ngưỡng kh&ocirc;ng gian thật tuyệt vời, tho&aacute;ng m&aacute;t, c&oacute; mạng internet Wifi, đầy đủ tiện nghi, kh&ocirc;ng gian rộng, sạch sẽ, ph&ograve;ng vệ sinh kh&eacute;p k&iacute;n, đồng thời Q&uacute;y kh&aacute;ch c&oacute; một kh&ocirc;ng gian ngắm cảnh l&uacute;c buổi s&aacute;ng tinh mơ hay buổi ho&agrave;ng h&ocirc;n ng&atilde; xuống h&ograve;a nguyện v&agrave;o trời xanh ng&oacute;ng nh&igrave;n đảo b&eacute; (An B&igrave;nh) huyền h&ograve;a như tr&ocirc;i giạc giữa biển khơi..</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>* Gi&aacute; ph&ograve;ng quạt m&aacute;y, Tivi, điều h&ograve;a, m&aacute;y nước n&oacute;ng:&nbsp;200.000 vnđ&nbsp;</strong><em>(ph&ograve;ng 1 giường)</em></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>* Gi&aacute; ph&ograve;ng quạt m&aacute;y, Tivi, Điều h&ograve;a, m&aacute;y nước n&oacute;ng:&nbsp;300.000 vnđ&nbsp;</strong><em>(ph&ograve;ng 2 giường)</em></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/khachsan/nha-nghi-ly-tri1.jpg\" style=\"height:99%; width:99%\" /></em></span></span><span style=\"color:#000000\"><strong>Dịch vụ đi k&egrave;m miền ph&iacute;:</strong></span></p>\r\n\r\n<p><span style=\"color:#000000\">- Đặt v&eacute; t&agrave;u cao tốc tuyến l&yacute; sơn sa kỳ, l&yacute; sơn đảo b&eacute; (An B&igrave;nh)</span></p>\r\n\r\n<p><span style=\"color:#000000\">- Hướng dẫn,&nbsp; tư vấn c&aacute;c địa điểm du lịch v&agrave; ăn uống tiết kiệm nhất</span></p>\r\n\r\n<p><span style=\"color:#000000\">- Xe đưa đ&oacute;n tại cấu cảng</span></p>\r\n\r\n<p><span style=\"color:#000000\">-&nbsp;M&aacute;y nước n&oacute;ng lạnh</span></p>\r\n\r\n<p><span style=\"color:#000000\"><strong>Dịch vụ kh&aacute;c:</strong></span></p>\r\n\r\n<p><span style=\"color:#000000\">- Hướng dẫn du lịch</span></p>\r\n\r\n<p><span style=\"color:#000000\">- Cho thu&ecirc; xe m&aacute;y, xe du lịch 16 chỗ.</span></p>\r\n\r\n<p><span style=\"color:#000000\">- Phục vụ phần ăn theo thực đơn hay theo y&ecirc;u cầu</span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Thăm quan khu nu&ocirc;i trồng t&ocirc;m h&ugrave;m tại l&yacute; sơn</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Thu mua đ&oacute;ng g&oacute;i đặt sản l&yacute; sơn như chả c&aacute;, hải sản, tỏi h&agrave;nh</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Thu đổi ngoại tệ</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Giặt l&agrave;</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Lễ t&acirc;n 24/24</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Để được tư vấn v&agrave; phục vụ tốt nhất</span></span></p>'),
(5, 'Khách Sạn Mường Thanh', 'Khách Sạn Mường Thanh - Du Lịch Lý Sơn', 'Tọa lạc ngay trung tâm hành chính của huyện đảo Lý Sơn, Mường Thanh Holiday Lý Sơn được thiết kế với tiêu chuẩn 4 sao với sự kết hợp của nền văn hóa hiện đại và không gian thiên nhiên hiền hòa biển xanh -  cát trắng -  nắng vàng.', 'du lịch lý sơn , du lịch đà nẵng , thuê xe quảng ngãi,thuê xe đà nẵng,khách sạn mường thanh', 1300000, 'upload/HnJG_mt.jpg', 'Lý Sơn', '<p><span style=\"color:#000000\"><strong>Kh&aacute;ch sạn&nbsp;Mường Thanh Holiday L&yacute; Sơn</strong>&nbsp;gồm 80 ph&ograve;ng, được trang bị c&aacute;c tiện nghi hiện đại gồm wifi miễn ph&iacute; ở c&aacute;c ph&ograve;ng, tivi m&agrave;n h&igrave;nh phẳng, điều h&ograve;a nhiệt độ, tủ quần &aacute;o, khu vực tiếp kh&aacute;ch, ph&ograve;ng tắm ri&ecirc;ng với bồn tắm v&ograve;i sen.</span></p>\r\n\r\n<p><span style=\"color:#000000\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/khachsan/1-1358547.jpg\" style=\"height:99%; width:99%\" /></span></p>\r\n\r\n<p><span style=\"color:#000000\">Kh&aacute;ch sạn cung cấp đa dạng dịch vụ gồm ph&ograve;ng thể dục, hồ bơi ngo&agrave;i trời, dịch vụ spa, massage, karaoke, nh&agrave; h&agrave;ng, qu&aacute;n c&agrave; ph&ecirc;, quầy bar cạnh hồ bơi, quầy lễ t&acirc;n 24 giờ.</span></p>\r\n\r\n<p><span style=\"color:#000000\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/khachsan/10-1358217.jpg\" style=\"height:99%; width:99%\" /></span></p>\r\n\r\n<p><span style=\"color:#000000\">Từ kh&aacute;ch sạn, Qu&yacute; kh&aacute;ch c&oacute; thể dễ d&agrave;ng đến c&aacute;c điểm tham quan nổi tiếng&nbsp;trong th&agrave;nh phố.</span></p>\r\n\r\n<p><span style=\"color:#000000\"><strong>Kh&aacute;ch sạn&nbsp;Mường Thanh Holiday L&yacute; Sơn&nbsp;</strong>tọa lạc ngay trung t&acirc;m h&agrave;nh ch&iacute;nh của huyện đảo L&yacute; Sơn, c&aacute;ch trung t&acirc;m th&agrave;nh phố Quảng Ng&atilde;i 20 km.<br />\r\n<img alt=\"\" src=\"http://lysonvn.com/public/upload/files/khachsan/2-1358594.jpg\" style=\"height:99%; width:99%\" /></span></p>\r\n\r\n<p><span style=\"color:#000000\">Những h&igrave;nh ảnh của kh&aacute;ch sạn mường thanh</span></p>\r\n\r\n<p><span style=\"color:#000000\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/hotel/19-1357496.jpg\" style=\"height:99%; width:99%\" /></span></p>\r\n\r\n<p><span style=\"color:#000000\">thiết kế tinh tế h&agrave;i h&ograve;a,&nbsp;<strong>Mường Thanh Holiday L&yacute; Sơn</strong>&nbsp;chắc chắn l&agrave; một điểm đến tuyệt vời khi bạn đặt ch&acirc;n đ&ecirc;n h&ograve;n đảo L&yacute; Sơn.</span></p>\r\n\r\n<p><span style=\"color:#000000\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/hotel/16-135848.jpg\" style=\"height:99%; width:99%\" /></span></p>\r\n\r\n<p><span style=\"color:#000000\">Kh&aacute;ch sạn Mường Thanh Holiday L&yacute; Sơn&nbsp;vị tr&iacute; đẹp, ph&ograve;ng v&agrave; chất lượng phục vụ đạt ti&ecirc;u chuẩn 5 sao ở một huyện đảo nhỏ</span>.</p>\r\n\r\n<p><span style=\"color:#000000\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/hotel/11-1358191.jpg\" style=\"height:99%; width:99%\" /></span></p>\r\n\r\n<p>&nbsp;</p>');

