CREATE TABLE `tin` (
  `id` int(11) NOT NULL,
  `tieuDe` varchar(500) COLLATE utf8mb4_unicode_ci NOT NULL,
  `title` varchar(500) COLLATE utf8mb4_unicode_ci NOT NULL,
  `description` varchar(500) COLLATE utf8mb4_unicode_ci NOT NULL,
  `keyword` varchar(400) COLLATE utf8mb4_unicode_ci NOT NULL,
  `img` varchar(500) COLLATE utf8mb4_unicode_ci NOT NULL,
  `date` date NOT NULL,
  `noiDung` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `maLoaiTin` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `tin`
--

INSERT INTO `tin` (`id`, `tieuDe`, `title`, `description`, `keyword`, `img`, `date`, `noiDung`, `maLoaiTin`) VALUES
(16, 'Lý Sơn -  Địa điểm du lịch lý tưởng ngắm hoàng hôn và bình minh', 'Lý Sơn -  Địa điểm du lịch lý tưởng ngắm hoàng hôn và bình minh', 'Chụp ảnh trước hoàng hôn hay bình minh không phải điều đơn giản kể cả với những tay máy chuyên nghiệp. Tuy nhiên, một bức ảnh thừa chút ánh sáng hay gương mặt tối hoàn toàn đôi khi cũng đem lại cho người xem những cảm giác lạ lẫm và được nhiều bạn trẻ thích thú.', 'du lịch lý sơn , du lịch đà nẵng , thuê xe quảng ngãi,thuê xe đà nẵng', 'upload\\7ilq_binhminh.jpg', '2019-04-03', '<p dir=\"ltr\"><span style=\"color:#000000\">Chụp ảnh trước ho&agrave;ng h&ocirc;n hay b&igrave;nh minh kh&ocirc;ng phải điều đơn giản kể cả với những tay m&aacute;y chuy&ecirc;n nghiệp. Tuy nhi&ecirc;n, một bức ảnh thừa ch&uacute;t &aacute;nh s&aacute;ng hay gương mặt tối ho&agrave;n to&agrave;n đ&ocirc;i khi cũng đem lại cho người xem những cảm gi&aacute;c lạ lẫm v&agrave; được nhiều bạn trẻ th&iacute;ch th&uacute;.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Thực tế, kh&ocirc;ng phải ở đ&acirc;u bạn cũng c&oacute; thể t&igrave;m được kh&ocirc;ng gian m&ecirc;nh m&ocirc;ng để chụp ho&agrave;ng h&ocirc;n hay b&igrave;nh minh như đảo L&yacute; Sơn, Quảng Ng&atilde;i.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/new2/news3/28.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>B&igrave;nh minh tr&ecirc;n đảo L&yacute; Sơn</em></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">B&igrave;nh minh từ h&ograve;n M&ugrave; Cu: Nằm ph&iacute;a đ&ocirc;ng của đảo lớn L&yacute; Sơn, b&atilde;i M&ugrave; Cu với ngọn hải đăng nổi tiếng cũng l&agrave; một trong những địa điểm ngắm b&igrave;nh minh tuyệt đẹp cho kh&aacute;ch du lịch. Tuy nhi&ecirc;n, việc tiếp cận khu vực n&agrave;y kh&ocirc;ng qu&aacute; dễ d&agrave;ng do hoạt động x&acirc;y dựng dang dở.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Bạn c&oacute; thể đi xe m&aacute;y hoặc đi taxi v&agrave; đi bộ một đoạn về ph&iacute;a hải đăng M&ugrave; Cu. Sau khi tới được hải đăng, du kh&aacute;ch sẽ đi bộ qua b&atilde;i đ&aacute; để tới điểm mũi của h&ograve;n M&ugrave; Cu.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Tới đ&acirc;y, bạn c&oacute; thể tha hồ tạo d&aacute;ng v&agrave; chụp h&igrave;nh tr&ecirc;n những mỏm đ&aacute; hay lợi dụng những vũng nước nhỏ để tạo n&ecirc;n những tấm h&igrave;nh phản chiếu ấn tượng.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/new2/news3/26.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Ho&agrave;ng h&ocirc;n tr&ecirc;n L&yacute; Sơn</em></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">B&igrave;nh minh từ khu vực hang C&acirc;u: C&aacute;ch kh&ocirc;ng xa vị tr&iacute; của đỉnh Thới Lới l&agrave; khu vực hang C&acirc;u. Tr&ecirc;n thực tế, hang C&acirc;u cũng l&agrave; điểm th&iacute;ch hợp cho cả việc ngắm ho&agrave;ng h&ocirc;n v&agrave; b&igrave;nh minh.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Sau khi băng qua những c&aacute;nh đồng tỏi, du kh&aacute;ch sẽ tới được khu vực hang C&acirc;u ph&iacute;a bờ Đ&ocirc;ng của đảo L&yacute; Sơn. Nơi đ&acirc;y được v&iacute; như &ldquo;Grand Cayon&rdquo; của Việt Nam với những v&aacute;ch đ&aacute; dựng đứng tr&ecirc;n bờ biển.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Khi mặt trời dần nh&ocirc; l&ecirc;n từ biển, cả khoảng kh&ocirc;ng m&ecirc;nh m&ocirc;ng như được d&aacute;t lớp v&agrave;ng rực rỡ, tạo n&ecirc;n sự kỳ vĩ cho bức tranh thi&ecirc;n nhi&ecirc;n hang C&acirc;u.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/new2/news3/27.jpg\" style=\"height:99%; width:99%\" /></span></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Cảnh đẹp L&yacute; Sơn</em></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Ho&agrave;ng h&ocirc;n từ tr&ecirc;n đỉnh Thới Lới: Đỉnh Thới Lới l&agrave; một trong những địa điểm h&uacute;t kh&aacute;ch nhất tr&ecirc;n đảo L&yacute; Sơn. Khi đứng từ tr&ecirc;n đỉnh, bạn c&oacute; thể chi&ecirc;m ngưỡng to&agrave;n bộ khung cảnh đảo L&yacute; Sơn hoặc hướng về ph&iacute;a biển.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Ch&iacute;nh v&igrave; vậy, đ&acirc;y được v&iacute; như địa điểm sống ảo th&iacute;ch hợp cho c&aacute;c bạn trẻ th&iacute;ch chụp ảnh &quot;deep&quot; trước ho&agrave;ng h&ocirc;n hay b&igrave;nh minh. Với c&aacute;c mỏm đ&aacute; cao hướng ra biển v&agrave; những b&atilde;i cỏ chạy d&agrave;i t&iacute;t tắp, khung cảnh nơi đ&acirc;y trở n&ecirc;n y&ecirc;n b&igrave;nh v&agrave; tĩnh lặng hơn khi ng&agrave;y sắp t&agrave;n.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Đỉnh n&uacute;i Thới Lới ph&ugrave; hợp cho cả việc chụp ho&agrave;ng h&ocirc;n v&agrave; b&igrave;nh minh nhưng nhiều bạn th&iacute;ch tới đ&acirc;y v&agrave;o l&uacute;c chiều t&agrave; v&igrave; kh&ocirc;ng kh&iacute; dễ chịu hơn sau một ng&agrave;y d&agrave;i lang thang khắp đảo.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Hơn nữa, khung cảnh ho&agrave;ng h&ocirc;n nh&igrave;n từ tr&ecirc;n cao xuống mặt biển thực sự ấn tượng với kh&ocirc;ng gian tr&agrave;n ngập sắc đỏ của mặt trời. Bạn c&oacute; thể v&ocirc; tư &quot;thả d&aacute;ng&quot; m&agrave; kh&ocirc;ng lo vướng qu&aacute; nhiều người với hướng nh&igrave;n ra mặt biển.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Tuy nhi&ecirc;n, điểm trừ duy nhất l&agrave; con đường l&ecirc;n đ&acirc;y kh&ocirc;ng c&oacute; điện v&agrave; khi xuống v&agrave;o buổi tối, bạn phải hết sức cẩn thận để tr&aacute;nh tai nạn kh&ocirc;ng đ&aacute;ng c&oacute;.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Ho&agrave;ng h&ocirc;n từ cổng T&ograve; V&ograve;: Tr&aacute;i với n&uacute;i Thới Lới, cổng T&ograve; V&ograve; l&agrave; địa điểm nằm ngay b&ecirc;n bờ biển nhưng về độ đ&ocirc;ng đ&uacute;c l&uacute;c ho&agrave;ng h&ocirc;n th&igrave; hơn hẳn so với đỉnh n&uacute;i Thới Lới.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Do vị tr&iacute; thuận lợi nằm kh&ocirc;ng c&aacute;ch xa bến cảng, khu vực cổng T&ograve; V&ograve; được nhiều du kh&aacute;ch lựa chọn để ngắm ho&agrave;ng h&ocirc;n với một khối đ&aacute; tạo th&agrave;nh m&aacute;i v&ograve;m, hướng về ph&iacute;a biển v&agrave; v&ocirc; v&agrave;n b&atilde;i đ&aacute; nhỏ nằm rải r&aacute;c xung quanh.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Khi ho&agrave;ng h&ocirc;n bu&ocirc;ng xuống, &aacute;nh chiều v&agrave;ng tạo n&ecirc;n khung cảnh v&ocirc; c&ugrave;ng kỳ vĩ cho cổng T&ograve; V&ograve;.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Điểm trừ lớn nhất tại cổng T&ograve; V&ograve; l&agrave; qu&aacute; đ&ocirc;ng, đặc biệt v&agrave;o dịp cuối tuần khiến du kh&aacute;ch kh&oacute; c&oacute; thể chụp được cho m&igrave;nh bức ảnh ưng &yacute;. Bạn c&oacute; thể chọn thời điểm muộn khi mọi người đ&atilde; ra về gần hết v&agrave; chọn chế độ chụp phơi s&aacute;ng với ch&acirc;n m&aacute;y ảnh.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Tuy nhi&ecirc;n, nếu trang thiết bị c&ograve;n hạn chế, du kh&aacute;ch c&oacute; thể t&igrave;m những g&oacute;c hẹp hơn hoặc đi xa về ph&iacute;a bờ đ&aacute; với kh&ocirc;ng gian rộng để thỏa sức s&aacute;ng tạo với bức h&igrave;nh của m&igrave;nh.</span></p>', 1),
(17, 'Hướng dẫn cách di chuyển đến đảo Lý Sơn từ A - Z', 'Hướng dẫn cách di chuyển đến đảo Lý Sơn từ A - Z', 'Kinh nghiệm khi đi du lịch lý sơn, những điều bổ ích cần phải biết trước khi đi Lý Sơn, và cùng lysontrip khám phá cách di chuyển đến đảo Lý Sơn', 'du lịch lý sơn , du lịch đà nẵng , thuê xe quảng ngãi,thuê xe đà nẵng', 'upload\\V73a_5 (1).jpg', '2019-04-02', '<p dir=\"ltr\"><span style=\"color:#000000\">Kinh nghiệm khi đi du lịch l&yacute; sơn, những điều bổ &iacute;ch cần phải biết trước khi đi L&yacute; Sơn, v&agrave; c&ugrave;ng lysontrip kh&aacute;m ph&aacute; c&aacute;ch di chuyển đến đảo L&yacute; Sơn</span></p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\">Tổng quan du lịch đảo L&yacute; Sơn</span></h2>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">L&yacute; Sơn l&agrave; một huyện đảo của tỉnh Quảng Ng&atilde;i,&nbsp;<strong>nằm c&aacute;ch cảng Sa Kỳ khoảng 30km</strong>, với diện t&iacute;ch gần 10km2 v&agrave; d&acirc;n số hơn 20.000 người. Đảo L&yacute; Sơn để lại ấn tượng kh&oacute; qu&ecirc;n trong l&ograve;ng du kh&aacute;ch với những ng&agrave;y nắng ấm m&ecirc;nh mang, những b&atilde;i biển trong vắt v&agrave; người d&acirc;n v&ocirc; c&ugrave;ng dễ mến.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/new2/news3/22.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Một g&oacute;c đảo L&yacute; Sơn nh&igrave;n từ tr&ecirc;n cao.</em></span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/new2/news3/23.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\">Đảo L&yacute; Sơn điểm đến hấp dẫn bạn n&ecirc;n gh&eacute; thăm một lần trong đời.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Huyện đảo L&yacute; Sơn gồm 3 h&ograve;n đảo l&agrave; Đảo Lớn, Đảo B&eacute; v&agrave; h&ograve;n M&ugrave; Cu. Đảo Lớn c&ograve;n gọi l&agrave; C&ugrave; Lao R&eacute;, l&agrave; trung t&acirc;m của L&yacute; Sơn. Đảo B&eacute; c&ograve;n c&oacute; t&ecirc;n gọi kh&aacute;c l&agrave; An B&igrave;nh. H&ograve;n M&ugrave; Cu ở ph&iacute;a đ&ocirc;ng, nằm s&aacute;t Đảo Lớn, l&agrave; đảo nhỏ nhất v&agrave; kh&ocirc;ng c&oacute; người ở.</span></p>\r\n\r\n<p dir=\"ltr\">&nbsp;</p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\">Thời điểm v&agrave;ng để du lịch đảo L&yacute; Sơn</span></h2>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Du kh&aacute;ch đến L&yacute; Sơn th&iacute;ch hợp nhất l&agrave; v&agrave;o 3 thời điểm sau:</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">&bull; M&ugrave;a h&egrave;: trong khoảng từ th&aacute;ng 4 đến th&aacute;ng 9 thời tiết kh&aacute; đẹp v&agrave; c&oacute; nắng, ph&ugrave; hợp cho việc đi biển.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">&bull; M&ugrave;a tỏi L&yacute; Sơn: bắt đầu được trồng v&agrave;o th&aacute;ng 9 v&agrave; thu hoạch v&agrave;o khoảng đầu th&aacute;ng 12</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">&bull; M&ugrave;a lễ hội: lễ khao thề l&iacute;nh Ho&agrave;ng Sa diễn ra v&agrave;o c&aacute;c ng&agrave;y 18-19-20 th&aacute;ng 3 (&acirc;m lịch)</span></p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\">Phương tiện di chuyển đến L&yacute; Sơn</span></h2>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Đảo L&yacute; Sơn nằm c&aacute;ch cảng Sa Kỳ - Quảng Ng&atilde;i khoảng 30km, v&igrave; vậy để đến được Đảo L&yacute; Sơn, ch&uacute;ng ta cần phải đến cảng Sa Kỳ - tỉnh Quảng Ng&atilde;i v&agrave; sau đ&oacute; mua v&eacute; t&agrave;u cao tốc để đi từ cảng Sa Kỳ ra đảo L&yacute; Sơn</span></p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\">Hướng dẫn đi đến cảng Sa Kỳ</span></h2>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\"><strong>M&aacute;y bay</strong></span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">S&acirc;n bay gần nhất để c&oacute; thể di chuyển ra cảng Sa Kỳ (nơi c&oacute; t&agrave;u cao tốc đi L&yacute; Sơn) l&agrave; s&acirc;n bay Chu Lai của Quảng Nam hoặc s&acirc;n bay Đ&agrave; Nẵng. Sau đ&oacute; c&aacute;c bạn n&ecirc;n thu&ecirc; một chuyến xe để đi bởi khoảng c&aacute;ch từ s&acirc;n bay Chu Lai đến cảng Sa Kỳ v&agrave;o qu&atilde;ng 50km.<strong>&nbsp;</strong>Hiện nay lysontrip&nbsp;<strong>đang c&oacute; giảm gi&aacute; 20%&nbsp;</strong>khi đặt xe đến Cảng Sa Kỳ, c&aacute;c bạn c&oacute; thể li&ecirc;n hệ, hoặc truy cập web để biết th&ecirc;m gi&aacute; khuyến m&atilde;i</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\"><strong>&Ocirc; t&ocirc; kh&aacute;ch</strong></span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Từ H&agrave; Nội v&agrave; S&agrave;i G&ograve;n c&oacute; rất nhiều h&atilde;ng xe chất lượng cao đến Quảng Ng&atilde;i, c&aacute;c bạn c&oacute; thể li&ecirc;n hệ c&aacute;c nh&agrave; xe để đặt v&eacute; trước (từ 2 &ndash; 5 ng&agrave;y t&ugrave;y v&agrave;o thời điểm chuyến đi của bạn, v&agrave;o những dịp nghỉ lễ th&igrave; cần đặt sớm nếu kh&ocirc;ng sẽ kh&ocirc;ng c&oacute; v&eacute;). Nhưng những chuyến đi n&agrave;y thường hay k&eacute;o d&agrave;i, khoảng 15 tiếng cho chuyến S&agrave;i G&ograve;n Quảng Ng&atilde;i</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\"><strong>T&agrave;u hỏa</strong></span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Mỗi ng&agrave;y từ 2 đầu H&agrave; Nội v&agrave; S&agrave;i G&ograve;n c&oacute; 5 chuyến t&agrave;u Thống Nhất ra v&agrave;o Bắc Nam, t&ugrave;y v&agrave;o khoảng thời gian của m&igrave;nh m&agrave; bạn c&oacute; thể chọn giờ t&agrave;u cho ph&ugrave; hợp (C&aacute;c t&agrave;u số lẻ đi từ H&agrave; Nội v&agrave;o, c&aacute;c t&agrave;u số chẵn đi từ S&agrave;i G&ograve;n ra).</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Sau khi di chuyển tới Quảng Ng&atilde;i nếu đo&agrave;n đ&ocirc;ng c&aacute;c bạn n&ecirc;n thu&ecirc; một chiếc &ocirc;t&ocirc; đi từ Trung t&acirc;m TP. Quảng Ng&atilde;i ra tới cảng Sa Kỳ (khoảng c&aacute;ch khoảng 20km) hoặc để tiết kiệm chi ph&iacute; c&aacute;c bạn c&oacute; thể sử dụng tuyến xe bu&yacute;t số 03 c&oacute; lịch tr&igrave;nh chạy Bến xe Quảng Ng&atilde;i &ndash; Cảng Sa Kỳ. Tuy nhi&ecirc;n lưu &yacute; với c&aacute;c bạn l&agrave; phải chuyến xe bu&yacute;t kh&ocirc;ng c&oacute; nhiều, c&aacute;c bạn phải tham khảo giờ bu&yacute;t v&agrave; gờ t&agrave;u cao tốc ra đảo L&yacute; Sơn trước khi di chuyển</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/new2/news3/24.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\">Nh&agrave; Ga cảng Sa Kỳ</span></p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\">Hướng dẫn di chuyển từ Cảng Sa Kỳ - Đảo L&yacute; Sơn</span></h2>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Mỗi ng&agrave;y từ cảng Sa Kỳ v&agrave; cảng L&yacute; Sơn đồng thời c&oacute; 3 chuyến t&agrave;u cao tốc ra đảo v&agrave; v&agrave;o đất liền. T&agrave;u rời cảng trong khoảng từ 7h30 đến 9h giờ. Ph&ograve;ng v&eacute; được mở cửa v&agrave;o 6h30 h&agrave;ng ng&agrave;y, mỗi người xếp h&agrave;ng sẽ được mua 2 v&eacute;, khi mua v&eacute; sẽ phải khai b&aacute;o họ t&ecirc;n, năm sinh, địa chỉ, số CMND.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Đối với c&aacute;c bạn đi đo&agrave;n đ&ocirc;ng c&oacute; thể đăng k&yacute; trước danh s&aacute;ch của đo&agrave;n (bao gồm đầy đủ c&aacute;c th&ocirc;ng tin như ph&iacute;a tr&ecirc;n) k&egrave;m theo số điện thoại đại diện của trưởng đo&agrave;n v&agrave; fax về cho ban quản l&yacute; cảng chậm nhất trước 15h30 của ng&agrave;y h&ocirc;m trước ng&agrave;y đi, khi đến mua v&eacute; cần mang theo danh s&aacute;ch gốc.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Để thuận tiện cho việc ra đảo L&yacute; Sơn, c&aacute;c bạn c&oacute; thể li&ecirc;n hệ với lysontrip để được hỗ trợ mua v&eacute; t&agrave;u, đặt xe đến cảng Sa Kỳ.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/new2/news3/25.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\">T&agrave;u ra đảo L&yacute; Sơn.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Để thuận tiện nhất khi di chuyển đến đảo L&yacute; Sơn, c&aacute;c bạn c&oacute; thể li&ecirc;n hệ hotline 18000079 hoặc chat với ch&uacute;ng t&ocirc;i để được tư vấn nhiệt t&igrave;nh nhất v&agrave; nhận được c&aacute;c m&atilde; giảm gi&aacute; khi đặt xe, đặt t&agrave;u cao tốc, đặt kh&aacute;ch sạn tại Đảo L&yacute; Sơn.</span></p>', 1),
(18, 'Cách phân biệt tỏi Lý Sơn thật giả của dân bản địa', 'Cách phân biệt tỏi Lý Sơn thật giả của dân bản địa', 'Chắc trong thời gian vừa qua bạn cũng nghe báo đài đăng tải nhiều thông tin về việc “Giải cứu tỏi Lý Sơn”. Hiện nay có rất nhiều thương hiệu buôn bán tỏi Lý Sơn giả khiến cho lượng tỏi thật ở Lý Sơn bị tồn đọng.', 'du lịch lý sơn , du lịch đà nẵng , thuê xe quảng ngãi,thuê xe đà nẵng', 'upload\\PTQp_toi.jpg', '2019-04-02', '<p dir=\"ltr\"><span style=\"color:#000000\">Chắc trong thời gian vừa qua bạn cũng nghe b&aacute;o đ&agrave;i đăng tải nhiều th&ocirc;ng tin về việc&nbsp;<strong>&ldquo;Giải cứu tỏi L&yacute; Sơn&rdquo;</strong>. Hiện nay c&oacute; rất nhiều thương hiệu<strong>&nbsp;bu&ocirc;n b&aacute;n tỏi L&yacute; Sơn</strong>&nbsp;giả khiến cho lượng tỏi thật ở L&yacute; Sơn bị tồn đọng. T&ocirc;i xin chia sẻ đến mọi người c&aacute;ch p<strong>h&acirc;n biệt tỏi L&yacute; Sơn</strong>&nbsp;thật giả của d&acirc;n địa phương để c&aacute;c bạn c&oacute; thể mua được tỏi ch&iacute;nh hiệu.</span></p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\"><strong>Tỏi L&yacute; Sơn</strong></span></h2>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Tỏi kh&ocirc;ng chỉ l&agrave; gia vị trong mỗi bữa ăn m&agrave; tỏi L&yacute; Sơn c&ograve;n được mệnh danh l&agrave;&nbsp;<strong>&ldquo;thần dược&rdquo;</strong>&nbsp;hỗ trợ điều trị rất nhiều bệnh, từ cảm c&uacute;m đến vi&ecirc;m xoang,... v&agrave; c&aacute;c bệnh tim mạch kh&aacute;c.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/new2/toi-ly-son-9.png\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Tỏi c&ocirc; đơn_ Đặc sản L&yacute; Sơn</em></span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Tỏi L&yacute; Sơn c&oacute; 2 loại</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\"><strong>Tỏi c&ocirc; đơn</strong>&nbsp;hay c&ograve;n gọi l&agrave; tỏi một t&eacute;p, ch&uacute;ng sinh trưởng trong m&ocirc;i trường đặc biệt kh&aacute;c v&oacute;i tỏi b&igrave;nh thường. Chỉ một số v&ugrave;ng đặc biệt mới c&oacute; thể c&oacute; loại tỏi n&agrave;y. To&agrave;n bộ gi&aacute; trị dinh dưỡng đều tập trung ở một t&eacute;p duy nhất n&ecirc;n c&oacute; gi&aacute; trị chữa bệnh rất cao.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\"><strong>Tỏi nhiều t&eacute;p</strong>: loại n&agrave;y phổ biến hơn v&agrave; dễ trồng hơn</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/new2/20.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Tỏi L&yacute; Sơn nhiều t&eacute;p</em></span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">C&ocirc;ng dụng của tỏi L&yacute; Sơn</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Tỏi L&yacute; Sơn rất tốt cho người d&ugrave;ng, từ gia vị trong bữa ăn đến &ldquo;thần dược&rdquo; hỗ trợ chữa c&aacute;c nh&oacute;m bệnh ch&iacute;nh như sau:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\">C&aacute;c bệnh về ti&ecirc;u h&oacute;a: ợ chua, đầy bụng, kh&oacute; ti&ecirc;u, vi&ecirc;m t&aacute; tr&agrave;ng, lo&eacute;t dạ d&agrave;y&hellip;</span></li>\r\n	<li><span style=\"color:#000000\">C&aacute;c bệnh về đường huyết: tiểu đường, huyết &aacute;p cao, tụ huyết khối, hở van tim, mỡ m&aacute;u&hellip;</span></li>\r\n	<li><span style=\"color:#000000\">H&ocirc; hấp: vi&ecirc;m họng, vi&ecirc;m phế quản, hen phế quản, vi&ecirc;m xoang mũi, vi&ecirc;m xoang mũi dị ứng, cảm c&uacute;m, ho&hellip;</span></li>\r\n	<li><span style=\"color:#000000\">Thấp khớp: sưng khớp, G&uacute;t (GOUT), v&ocirc;i h&oacute;a c&aacute;c khớp, thấp khớp, mỏi xương cốt&hellip;</span></li>\r\n	<li><span style=\"color:#000000\">Chống ung thư: ung thư dạ d&agrave;y, ung thư phổi, ung thư gan,&hellip;</span></li>\r\n	<li><span style=\"color:#000000\">Ngo&agrave;i ra tỏi c&ocirc; đơn c&ograve;n c&oacute; đặc t&iacute;nh s&aacute;t khuẩn, giảm sưng tấy, vết thương do muỗi đốt,&hellip; V&agrave;&nbsp;tỏi c&ocirc; đơn&nbsp;c&ograve;n c&oacute; t&aacute;c dụng giống như thuốc kh&aacute;ng sinh.</span></li>\r\n</ul>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\"><strong>C&aacute;ch ph&acirc;n biệt tỏi L&yacute; Sơn</strong></span></h2>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Ch&iacute;nh v&igrave; những t&aacute;c dụng tuyệt vời n&agrave;y m&agrave; tr&ecirc;n thị trường xuất hiện rất nhiều loại tỏi rởm, nh&aacute;i thương hiệu tỏi L&yacute; Sơn. Nếu người ti&ecirc;u d&ugrave;ng kh&ocirc;ng c&oacute; c&aacute;c kiến thức về tỏi L&yacute; Sơn sẽ dễ bị đ&aacute;nh lừa.</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Đặc điểm dễ nhận diện nhất</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\"><strong>Tỏi &ldquo;c&ocirc; đơn&rdquo;</strong>&nbsp;nhận biết bằng phần vỏ tỏi c&oacute; m&agrave;u trắng v&agrave; rất mỏng. C&ograve;n những loại tỏi kh&ocirc;ng phải tỏi L&yacute; Sơn c&oacute; m&agrave;u t&iacute;a t&iacute;m.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Về k&iacute;ch cỡ, tỏi L&yacute; Sơn nhỏ chỉ bằng ng&oacute;n tay trỏ đối với tỏi 1 v&agrave; bằng ng&oacute;n ch&acirc;n c&aacute;i với loại tỏi nhiều t&eacute;p. Bộ rễ tỏi nhiều v&agrave; chi&ecirc;m diện t&iacute;ch lớn, cọng rễ c&oacute; m&agrave;u đen hoặc v&agrave;ng sậm, to v&agrave; dai.</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Nhận biết bằng c&aacute;ch nếm thử</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Khi nếm thử<strong>&nbsp;tỏi L&yacute; Sơn</strong>&nbsp;sẽ c&oacute; m&ugrave;i rất đặc trưng v&agrave; dai dẳng, để lại hậu vị. Hơn nữa, tỏi L&yacute; Sơn khi ăn c&oacute; vị ngọt nhẹ, &iacute;t cay nhưng đậm đ&agrave;, đặc biệt kh&ocirc;ng để lại m&ugrave;i h&ocirc;i như những loại tỏi th&ocirc;ng thường.</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">V&igrave; sao h&agrave;ng nh&aacute;i lại xuất hiện nhiều như vậy?</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Với t&acirc;m l&yacute; ưa đồ chất lượng nhưng gi&aacute;&nbsp;<strong>&ldquo;mềm&rdquo;</strong>&nbsp;của người d&acirc;n Việt Nam đ&atilde; khiến kh&ocirc;ng &iacute;t kh&aacute;ch h&agrave;ng mua phải tỏi rởm. Mới đ&acirc;y tại Th&agrave;nh phố Hồ Ch&iacute; Minh v&agrave; c&aacute;c tỉnh th&agrave;nh kh&aacute;c xuất hiện rất nhiều tỏi mang &ldquo;m&aacute;c&rdquo; tỏi L&yacute; Sơn, tỏi &ldquo;c&ocirc; đơn&rdquo; với gi&aacute; chỉ 80.000 đồng. Trong khi loại tỏi 1 t&eacute;o ch&iacute;nh hiệu l&ecirc;n tới 800.000 đồng/kg, tỏi nhiều t&eacute;p th&igrave; gi&aacute; cũng khoảng 150.000 đồng/kg.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Nhiều người ham rẻ mua phải những loại tỏi kh&ocirc;ng r&otilde; xuất xứ vừa tốn tiền lại kh&ocirc;ng mang lại hiệu quả mong muốn.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/new2/toi.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Ph&acirc;n biệt Tỏi L&yacute; Sơn với c&aacute;c tỏi kh&aacute;c</em></span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Khuyến c&aacute;o kh&ocirc;ng n&ecirc;n mua tỏi Trung Quốc</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Tr&ecirc;n thị trường hiện nay c&oacute; loại tỏi t&eacute;p to, vỏ m&agrave;u trắng v&agrave;ng của&nbsp;<strong>Trung Quốc</strong>, vị hăng m&agrave; kh&ocirc;ng thơm v&agrave; được khử tr&ugrave;ng bằng&nbsp;<strong>methyl bromide&nbsp;</strong>trước khi xuất khẩu. Đ&acirc;y l&agrave; chất diệt s&acirc;u bọ rất độc hại cho con người. N&oacute; c&oacute; thể hủy hoại hệ h&ocirc; hấp v&agrave; cơ quan thần kinh trung ương, thậm ch&iacute; g&acirc;y tử vong.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Nếu như kh&ocirc;ng c&oacute; điều kiện&nbsp;<strong>mua tỏi L&yacute; Sơn</strong>&nbsp;để d&ugrave;ng, bạn cũng kh&ocirc;ng n&ecirc;n mua tỏi Trung Quốc m&agrave; h&atilde;y t&igrave;m đến c&aacute;c loại tỏi trồng ở c&aacute;c v&ugrave;ng kh&aacute;c của việt Nam như Ninh Thuận, Kh&aacute;nh H&ograve;a,.. để tr&aacute;nh những rủi ro, bảo vệ sức khỏe ch&iacute;nh m&igrave;nh v&agrave; người th&acirc;n.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">B&ecirc;n cạnh đ&oacute;, để chắc chắn rằng bạn mua tỏi ch&iacute;nh h&atilde;ng th&igrave; sao kh&ocirc;ng l&agrave;m một chuyến du lịch ra L&yacute; Sơn, tận mắt nh&iacute;n những ruộng tỏi v&agrave; mua trực tiếp của người d&acirc;n L&yacute; Sơn để tr&aacute;nh c&aacute;c trường hợp tiền mất tật mang. Hay li&ecirc;n hệ hotline 1800 0076 để biết th&ecirc;m chi tiết.</span></p>', 3),
(19, 'Nên thưởng thức đặc sản gì khi du lịch đảo Lý Sơn?', 'Nên thưởng thức đặc sản gì khi du lịch đảo Lý Sơn?', 'Lý sơn là một huyện đảo thuộc Tỉnh Quảng Ngãi, được bao bọc bốn bề là biển. Vì vậy, nơi đây nổi tiếng với những loại hải sản tươi sống. Và đặc biệt là “Tỏi”- đặc sản vang danh mọi miền. Chúng tôi sẽ điểm qua 10 món ăn không thể bỏ qua khi du lịch tại Lý Sơn cho các bạn thưởng thức nhé!', 'du lịch lý sơn , du lịch đà nẵng , thuê xe quảng ngãi,thuê xe đà nẵng', 'upload\\sS7s_Vào-bếp-làm-món-ngon-từ-cua-Huỳnh-Đế-đặc-sản-Bình-Định-3.jpg', '2019-04-02', '<p dir=\"ltr\"><span style=\"color:#000000\"><strong>L&yacute; sơn</strong>&nbsp;l&agrave; một huyện đảo thuộc Tỉnh Quảng Ng&atilde;i, được bao bọc bốn bề l&agrave; biển. V&igrave; vậy, nơi đ&acirc;y nổi tiếng với những loại hải sản tươi sống. V&agrave; đặc biệt l&agrave; &ldquo;Tỏi&rdquo;- đặc sản vang danh mọi miền. Ch&uacute;ng t&ocirc;i sẽ điểm qua 10 m&oacute;n ăn kh&ocirc;ng thể bỏ qua khi du lịch tại L&yacute; Sơn cho c&aacute;c bạn thưởng thức nh&eacute;!</span></p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\"><strong>7 m&oacute;n ăn n&ecirc;n thưởng thức khi đặt ch&acirc;n đến L&yacute; Sơn</strong></span></h2>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">1.Gỏi rong biển</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Đ&acirc;y l&agrave; một trong những đặc sản của biển đảo L&yacute; Sơn với hương vị đậm đ&agrave; của biển cả nơi đ&acirc;y sẽ l&agrave;m bạn kh&oacute; qu&ecirc;n nếu đ&atilde; từng thưởng qua một lần. Những cọng rong biển th&acirc;n tr&ograve;n, rẽ nh&aacute;nh như những c&agrave;nh san h&ocirc;, xanh trong trộn lẫn giữa những cọng rau h&uacute;ng quế v&agrave; lớp đậu phộng , h&agrave;nh phi b&ecirc;n tr&ecirc;n dậy l&ecirc;n một m&ugrave;i thơm k&iacute;ch th&iacute;ch khứu gi&aacute;c của ch&uacute;ng ta.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/new2/16.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Gỏi rong biển tươi ngon, đậm đ&agrave; hương vị biển</em></span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">2.Gỏi Tỏi</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">L&yacute; Sơn được mệnh danh l&agrave; vương quốc tỏi, c&acirc;y tỏi ngo&agrave;i lấy củ c&ograve;n chế biến th&agrave;nh m&oacute;n gỏi v&ocirc; c&ugrave;ng hấp dẫn. Gỏi tỏi L&yacute; Sơn được l&agrave;m từ th&acirc;n tỏi đực, tỏi nhổ l&ecirc;n chỉ lấy phần th&acirc;n, lột bỏ lớp vỏ ngo&agrave;i c&ugrave;ng, rửa sạch rồi đem hấp c&aacute;ch thủy cho ch&iacute;n, sau đ&oacute; trộn với c&aacute;c loại gia vị, rắc th&ecirc;m một &iacute;t đậu phộng l&agrave; đ&atilde; c&oacute; m&oacute;n gỏi tỏi với hương cay nồng, ngon tuyệt.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/new2/17.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Gỏi tỏi L&yacute; Sơn</em></span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">3.Ốc Tượng</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Ốc Tượng l&agrave; m&oacute;n hải sản nổi tiếng nhất ở đ&acirc;y v&agrave; được rất nhiều du kh&aacute;ch ưa chuộng. Ốc Tượng kh&ocirc;ng phải dễ bắt, tuy l&agrave; ốc biển nhưng kh&ocirc;ng phải biển n&agrave;o cũng c&oacute;. L&yacute; Sơn c&oacute; lẽ được xem l&agrave; qu&ecirc; hương của lo&agrave;i ốc n&agrave;y. Loại ốc tượng to v&agrave; ngon nhất phải chọn loại lớn cỡ b&agrave;n tay x&ograve;e ra, nặng khoảng nửa k&yacute;. Người d&acirc;n nơi đ&acirc;y c&oacute; thể chế biến loại ốc n&agrave;y th&agrave;nh c&aacute;c m&oacute;n ăn cực kỳ đơn giản m&agrave; cũng cực ngon.</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">4.Ốc Cừ</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">N&oacute; c&oacute; t&ecirc;n gọi như vậy sở dĩ v&igrave; loại ốc n&agrave;y c&oacute; miếng vảy che trước miệng tr&ograve;n tr&ograve;n giống chiếc c&uacute;c &aacute;o cứng như x&agrave; cừ, thịt ốc gi&ograve;n v&agrave; rất ngọt. ốc Cừ ở Đảo L&yacute; Sơn ngon hơn hẳn những v&ugrave;ng kh&aacute;c. Ốc được bắt l&ecirc;n từ biển, đặc biệt những nơi s&oacute;ng c&agrave;ng lớn th&igrave; ốc c&agrave;ng ngon. Khi thủy triều r&uacute;t xuống thấp, chỉ cần dạo m&eacute; b&atilde;i biển đ&atilde; c&oacute; thể bắt được ch&uacute;ng, nhưng chỉ l&agrave; loại ốc nhỏ. Muốn c&oacute; ốc to bạn phải lặn xuống thật s&acirc;u. Ốc Cừ c&oacute; thể đem nướng, luộc hay x&agrave;o sả ớt đều ngon v&agrave; vẫn giữ được độ ngọt của ốc, giữ được hương vị của loại đặc sản v&ugrave;ng biển đảo L&yacute; Sơn.</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">5.Cua Huỳnh Đế</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Đến L&yacute; Sơn bạn kh&ocirc;ng thể bỏ qua m&oacute;n đặc sản tươi ngon n&agrave;y. Cua Huỳnh Đế được t&ocirc;n xưng l&agrave; vua của c&aacute;c loại cua bởi bộ gi&aacute;p d&agrave;y v&agrave; cứng, m&agrave;u đỏ hồng như chiến b&agrave;o, c&agrave;ng to, cạnh sắc l&eacute;m như dao. Cua Huỳnh Đế l&agrave; đặc sản tiến vua trong lịch sử bởi thịt mềm, c&agrave;ng ăn c&agrave;ng ngon, thơm v&agrave; bổ dưỡng.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/new2/18.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Cua Huỳnh đế L&yacute; Sơn</em></span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">6.Ch&aacute;o Nhum biển</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Nhum c&ograve;n được gọi l&agrave; cầu gai hay nh&iacute;m biển với h&igrave;nh d&aacute;ng x&ugrave; x&igrave; y hệt ch&ugrave;m gai. Ch&uacute;ng thường sống b&aacute;m ở những bụi l&aacute; huệ quanh đảo. Sau khi đ&aacute;nh bắt th&igrave; những người d&acirc;n phải bổ đ&ocirc;i con nhum một c&aacute;ch cẩn thận nếu kh&ocirc;ng muốn bị gai đ&acirc;m. Gỡ lớp thịt chấm với muối chanh v&agrave; m&ugrave; tạt th&igrave; c&ograve;n g&igrave; bằng, ăn sống ngay khi mới vớt l&ecirc;n khỏi mặt nước hoặc đem trộn với trứng chưng mặn để ăn cơm. C&oacute; nhiều c&aacute;ch chế biến nhum biển nhưng ngon nhất vẫn l&agrave; m&oacute;n ch&aacute;o nhum, ăn n&oacute;ng, hương vị của m&oacute;n ăn n&agrave;y rất đặc biệt, c&oacute; ch&uacute;t ngọt, ch&uacute;t mặn, h&ograve;a lẫn vị b&eacute;o.</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">7.C&aacute; T&agrave; Ma</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">C&aacute; T&agrave; ma l&agrave; một loại c&aacute; sống ở biển, da c&oacute; vảy m&agrave;u n&acirc;u đen, th&acirc;n dẹt, h&igrave;nh giống như con c&aacute; r&ocirc; phi nước ngọt nhưng to hơn nhiều. Theo người d&acirc;n ở đảo, n&oacute; c&oacute; t&ecirc;n gọi như vậy sở dĩ v&igrave; ch&uacute;ng sống chủ yếu ở c&aacute;c g&agrave;nh rạn, rất lanh kh&ocirc;n, kh&oacute; đ&aacute;nh bắt n&ecirc;n d&acirc;n gian gọi như vậy.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/new2/19.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>C&aacute; T&agrave; ma</em></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Những m&oacute;n ăn tr&ecirc;n c&oacute; k&iacute;ch th&iacute;ch vị gi&aacute;c của c&aacute;c bạn kh&ocirc;ng? H&atilde;y đến với L&yacute; Sơn ngay h&ocirc;m nay để thưởng thức những m&oacute;n ăn ngon, lạ, chỉ c&oacute; ở<strong>&nbsp;L&yacute; Sơn&nbsp;</strong>m&agrave; th&ocirc;i.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Li&ecirc;n hệ<strong>&nbsp;hotline 1800 0076</strong>&nbsp;để được biết th&ecirc;m th&ocirc;ng tin chi tiết về c&aacute;c tour du lịch L&yacute; Sơn nh&eacute;.</span></p>', 2),
(20, 'Chia sẻ kinh nghiệm đi Lý Sơn tiết kiệm chi phí nhất', 'Chia sẻ kinh nghiệm đi Lý Sơn tiết kiệm chi phí nhất', 'Bạn làm gì vào những ngày nghỉ, hãy cùng bạn bè, gia đình đến Lý Sơn du lịch thôi nào. Bạn sẽ được tận hưởng không gian mát lành, đậm mùi biển cả, ngắm những phong cảnh tuyệt đẹp, ăn hải sản thỏa thích.', 'du lịch lý sơn , du lịch đà nẵng , thuê xe quảng ngãi,thuê xe đà nẵng', 'upload\\95A8_re.jpg', '2019-04-02', '<p><span style=\"color:#000000\">Bạn l&agrave;m g&igrave; v&agrave;o những ng&agrave;y nghỉ, h&atilde;y c&ugrave;ng bạn b&egrave;, gia đ&igrave;nh đến L&yacute; Sơn du lịch th&ocirc;i n&agrave;o. Bạn sẽ được tận hưởng kh&ocirc;ng gian m&aacute;t l&agrave;nh, đậm m&ugrave;i biển cả, ngắm những phong cảnh tuyệt đẹp, ăn hải sản thỏa th&iacute;ch.</span></p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\"><strong>H&agrave;nh tr&igrave;nh ra đảo L&yacute; Sơn gi&aacute; rẻ</strong></span></h2>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">H&agrave;nh tr&igrave;nh ra cảng Sa kỳ</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Sau khi đ&aacute;p s&acirc;n bay Chu Lai, bạn c&oacute; thể đi xe trung chuyển miễn ph&iacute; từ s&acirc;n bay Chu Lai đến th&agrave;nh phố Quảng Ng&atilde;i. Khi đến bến xe bạn c&oacute; hai sự lựa chọn để ra cảng Sa Kỳ:</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"color:#000000\">Bắt taxi đến cảng Sa Kỳ nếu đi đ&ocirc;ng.</span></li>\r\n	<li><span style=\"color:#000000\">Đi xe bus c&oacute; ghi r&otilde; cảng Sa Kỳ để tiết kiệm chi ph&iacute; nh&eacute;</span></li>\r\n</ol>\r\n\r\n<p><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news1/10.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#000000\"><em>Đi t&agrave;u cao tốc ra L&yacute; Sơn</em></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Sau khi tới cảng, bạn h&atilde;y xếp h&agrave;ng mua v&eacute; v&agrave; đi ăn cơm trước khi ra đảo nh&eacute;.</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Kiếm chỗ ở</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Trước khi ra&nbsp;<strong>L&yacute; Sơn</strong>&nbsp;bạn n&ecirc;n đặt ph&ograve;ng trước để tr&aacute;nh trường hợp hết ph&ograve;ng, kh&ocirc;ng c&oacute; chỗ ngủ. Ngo&agrave;i L&yacute; Sơn c&oacute; rất nhiều kh&aacute;ch sạn chất lượng như central Hotel 3 sao, nh&agrave; nghỉ Hương Cảng, Tường Vy Hotel, Kh&aacute;ch sạn L&yacute; Sơn, Song B&igrave;nh,...</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Bạn cần nhận ph&ograve;ng, sắp xếp h&agrave;nh l&yacute; v&agrave; nghỉ ngơi hồi sức trước khi đi tham quan đảo L&yacute; Sơn để chuyển đi vui hơn.</span></p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\"><strong>Lịch tr&igrave;nh vui chơi tại L&yacute; Sơn</strong></span></h2>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Ng&agrave;y đầu</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">H&ocirc;m đầu ti&ecirc;n mới ra, sau khi lấy ph&ograve;ng v&agrave; ổn định mọi thứ. Chiều đ&oacute;, bạn c&oacute; thể đi dạo ngắm ho&agrave;ng h&ocirc;n ngay tr&ecirc;n cảng, ho&agrave;ng h&ocirc;n ở L&yacute; Sơn rất đẹp. Bạn h&atilde;y chịu kh&oacute; tr&egrave;o l&ecirc;n ngồi tr&ecirc;n bờ k&egrave;, tr&ecirc;n đ&oacute; gi&oacute; m&aacute;t rượi, bạn c&oacute; thể vừa ngắm cảnh vừa nghe nhạc.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news1/13.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Ho&agrave;ng h&ocirc;n tr&ecirc;n cảng L&yacute; Sơn</em></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Tối đến, bạn h&atilde;y thu&ecirc; cho m&igrave;nh một chiếc xe m&aacute;y ngay tại kh&aacute;ch sạn để thuận tiện cho việc đi lại. Gi&aacute; thu&ecirc; xe thường l&agrave; 150k một ng&agrave;y, nhớ đội mũ bảo hiểm để đảm bảo an to&agrave;n nh&eacute;.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Tối đến đi h&aacute;t Karaoke ở nh&agrave; h&agrave;ng hải &Acirc;u để giải tỏa stress v&agrave; l&ecirc;n tinh thần cho cuộc vui ng&agrave;y mai.</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Ng&agrave;y thứ 2: Ngắm b&igrave;nh minh ở Cổng T&ograve; V&ograve;- Ch&ugrave;a Hang- Đỉnh Thới Lới- Hang C&acirc;u- Giếng Am- Thăm Tỏi L&yacute; Sơn</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Bạn n&ecirc;n dậy sớm để ngắm b&igrave;nh minh, đừng ham ngủ nh&eacute;. 5h s&aacute;ng: c&ugrave;ng nhau đi ngắm b&igrave;nh minh ở cổng T&ograve; V&ograve;, sau đ&oacute; hẳn về ăn s&aacute;ng ở gần chợ. Đến 7h bắt đầu đi thăm quan c&aacute;c địa điểm kh&aacute;c tr&ecirc;n đảo. Đầu ti&ecirc;n l&agrave; ch&ugrave;a Hang- ng&ocirc;i ch&ugrave;a nằm gần bờ biển, cực kỳ đẹp. Khi v&agrave;o ch&ugrave;a bạn n&ecirc;n ăn mặc k&iacute;n đ&aacute;o, đến khi chụp ảnh hẳn thay đồ.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Tiếp tục đi l&ecirc;n n&uacute;i Thới Lới, chỗ c&oacute; cột cờ Việt Nam rồi ngắm huyện đảo L&yacute; Sơn từ tr&ecirc;n cao xuống v&agrave; cảnh biển tuyệt đẹp nơi đ&acirc;y. Tiếp theo tới Hang C&acirc;u, ở đ&acirc;y c&oacute; v&aacute;ch đ&aacute; uốn lượn rất kỳ vỹ c&oacute; thể leo l&ecirc;n đ&oacute; chụp ảnh, nước biển trong xanh miễn b&agrave;n c&oacute; thể xuống tắm nhưng chỉ n&ecirc;n tắm gần bờ v&igrave; s&oacute;ng kh&aacute; to.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news1/14.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Thả m&igrave;nh tr&ecirc;n d&ograve;ng nước m&aacute;t lạnh tại L&yacute; Sơn</em></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Đến 16h về nh&agrave; nghỉ ngơi hoặc cũng c&oacute; thể tham quan c&aacute;c địa điểm như &Acirc;m linh tự, giếng Vua, ch&ugrave;a Đục,... Cho đến 17h ngắm ho&agrave;ng h&ocirc;n ở cổng T&ograve; V&ograve;.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">18h30 c&ugrave;ng nhau lượn lờ c&aacute;c nh&agrave; h&agrave;ng hải sản thưởng thức c&aacute;c loại hải sản tươi sống c&aacute;, ốc, mực,... c&oacute; thể tự nướng tại chổ , ngo&agrave;i ra c&ograve;n c&oacute; g&agrave;, lẩu th&aacute;i,... rất rẻ m&agrave; lại tươi ngon. Đến 20h uống cafe ở kh&aacute;ch sạn L&yacute; Sơn central, tầng 3 view hướng ra biển rất đẹp, gi&oacute; m&aacute;t rượi. Kết th&uacute;c một ng&agrave;y thật bận rộn m&agrave; vui vẻ phải kh&ocirc;ng n&agrave;o.</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Ng&agrave;y 3: Dạo chơi Đảo B&eacute; th&ocirc;i n&agrave;o</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">8h-8h30 l&ecirc;n t&agrave;u ra đảo B&eacute; tham quan th&ocirc;i với gi&aacute; v&eacute; khứ hồi chỉ 50k/người. Sau đ&oacute; đi xe tuk tuk 10k/người/lượt để đến được b&atilde;i tắm đảo B&eacute;.b&atilde;i tắm n&agrave;y được v&iacute; l&agrave; Maldives của Việt Nam. Từ đẹp kh&ocirc;ng đủ để tả nước biển ở đ&acirc;y, như l&agrave; &ldquo;thi&ecirc;n đường mặt đất&rdquo; vậy. Sau khi tắm biển xong, mọi người c&oacute; thể đi tham quan c&aacute;c địa điểm kh&aacute;c như rặng dừa, ngắm san h&ocirc;, đến vườn cam rừng h&aacute;i cam,... Đến 10h50 l&ecirc;n xe tuk tuk quay lại bến t&agrave;u về đảo lớn.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Sau khi đ&atilde; dạo chơi hết mọi nơi tr&ecirc;n đảo L&yacute; Sơn th&igrave; cũng đến l&uacute;c xếp đồ , trả ph&ograve;ng chuẩn bị về đất liền rồi. Nếu c&ograve;n tiếc nuối bạn đừng qu&ecirc;n mua đặc sản nơi đ&acirc;y về l&agrave;m qu&agrave; v&agrave; vật kỷ niệm nh&eacute;.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news1/15.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Nếu bạn vẫn chưa biết l&agrave;m thế n&agrave;o để đặt v&eacute; xe, v&eacute; t&agrave;u, book ph&ograve;ng ra L&yacute; Sơn nhanh nhất hay li&ecirc;n hệ hotline 1800 0076 để được tư vấn c&aacute;c dịch vụ cho chuyến du lịch L&yacute; Sơn ho&agrave;n hảo hơn.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\"><strong>C&Ocirc;NG TY TNHH LYSONTRIP CH&Agrave;O MỪNG BẠN ĐẾN VỚI L&Yacute; SƠN</strong></span></p>', 1),
(21, 'Kinh nghiệm du lịch Đảo Lý Sơn', 'Kinh nghiệm du lịch Đảo Lý Sơn', 'Hãy xách ba lô lên và thực hiện chuyến du lịch Lý Sơn ngay thôi nào. bạn sẽ thích thú có một kỳ nghỉ mát cực kỳ vui vẻ và thoải mái tại nơi đây. Để chuyến đi được trọn vẹn hãy xem', 'du lịch lý sơn , du lịch đà nẵng , thuê xe quảng ngãi,thuê xe đà nẵng', 'upload\\hJuB_kinhnghiem.jpg', '2019-04-02', '<p><span style=\"color:#000000\">H&atilde;y x&aacute;ch ba l&ocirc; l&ecirc;n v&agrave; thực hiện chuyến du lịch L&yacute; Sơn ngay th&ocirc;i n&agrave;o. bạn sẽ th&iacute;ch th&uacute; c&oacute; một kỳ nghỉ m&aacute;t cực&nbsp;kỳ vui vẻ v&agrave; thoải m&aacute;i tại nơi đ&acirc;y. Để chuyến đi được trọn vẹn h&atilde;y xem</span></p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\">Thời điểm l&yacute; tưởng nhất du lịch L&yacute; Sơn</span></h2>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Đảo L&yacute; Sơn với kh&iacute; hậu h&agrave;i h&ograve;a, vẻ đẹp thơ mộng n&ecirc;n mọi người c&oacute; thể đi du lịch quanh năm. Tuy nhi&ecirc;n, thời điểm đẹp nhất để tham quan l&agrave; v&agrave;o m&ugrave;a h&egrave;, từ th&aacute;ng 4 đến th&aacute;ng 9 dương lịch với thời tiết kh&aacute; đẹp, nhiều nắng v&agrave; gi&oacute; rất th&iacute;ch hợp cho việc đi du lịch biển. C&ograve;n v&agrave;o th&aacute;ng 11,12 khi đến L&yacute; Sơn, bạn sẽ được tận mắt chứng kiến những thửa ruộng tỏi xanh bao la, b&aacute;t ng&aacute;t.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news1/9.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Bờ biển trong xanh ở L&yacute; Sơn</em></span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Phương tiện đi đến đảo L&yacute; Sơn</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Di chuyển bằng xe m&aacute;y sẽ gi&uacute;p bạn tiết kiệm kh&aacute; nhiều thời gian trong việc đi lại đồng thời c&oacute; th&ecirc;m thời gian tận hưởng chuyến du lịch L&yacute; Sơn của m&igrave;nh. Bạn cũng c&oacute; thể đi m&aacute;y bay đến s&acirc;n bay Chu Lai sau đ&oacute; bắt taxi hoặc xe bus từ Chu Lai v&agrave;o Quảng Ng&atilde;i . Nếu đi đ&ocirc;ng người th&igrave; Xe kh&aacute;ch sẽ l&agrave; phương tiện tiện lợi nhất cho c&aacute;c bạn.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Khi đến cảng Sa Kỳ- c&aacute;ch th&agrave;nh phố Quảng Ng&atilde;i 20km, bạn đi ra đảo bằng t&agrave;u cao tốc chừng 30 ph&uacute;t l&agrave; tới.</span></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news1/10.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Đi t&agrave;u cao tốc ra L&yacute; Sơn</em></span></p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\"><strong>Đến Đảo&nbsp;L&yacute; Sơn ăn g&igrave;?</strong></span></h2>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Gỏi tỏi</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">L&yacute; Sơn nổi tiếng l&acirc;u đời l&agrave; xứ h&agrave;ng tỏi n&ecirc;n khi đến L&yacute; Sơn bạn kh&ocirc;ng thể bỏ qua đặc sản l&agrave;m từ tỏi L&yacute; Sơn trứ danh n&agrave;y. Tỏi được chọn l&agrave;m m&oacute;n n&agrave;y l&agrave; tỏi đực, kh&ocirc;ng tạo m&uacute;i, chỉ lấy phần th&acirc;n. Qua qu&aacute; tr&igrave;nh chế biến c&ocirc;ng phu ăn k&egrave;m với b&aacute;nh tr&aacute;ng tương th&igrave; c&ograve;n g&igrave; bằng. Gỏi tỏi kh&ocirc;ng chỉ l&agrave; đặc sản đơn thuần m&agrave; c&ograve;n rất tốt cho sức khỏe, n&ecirc;n đ&acirc;y l&agrave; m&oacute;n ăn kh&ocirc;ng thể bỏ qua khi ăn ở l&yacute; Sơn.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news1/11.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Gỏi tỏi- đặc sản L&yacute; Sơn</em></span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Hải sản</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Đến với L&yacute; Sơn, v&ugrave;ng biển cả th&igrave; hải sản tươi ngon lu&ocirc;n k&iacute;ch th&iacute;ch vị gi&aacute;c của tất cả du kh&aacute;ch. C&aacute; T&agrave; M&agrave;, ch&aacute;o Nhum biển, gỏi sứa, gỏi rong biển, chả c&aacute; l&yacute; sơn,.. l&agrave; những m&oacute;n ăn hấp dẫn đầy bổ dưỡng ở nơi n&agrave;y.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">C&aacute;c loại hải sản tươi sống được bắt l&ecirc;n chế biến trực tiếp th&igrave; c&ograve;n g&igrave; bằng. Đ&acirc;y l&agrave; thi&ecirc;n đường hải sản, bạn c&oacute; thể thỏa m&atilde;n niềm đam m&ecirc; hải sản khi đến nơi n&agrave;y.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news1/12.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Chỗ nghỉ tr&ecirc;n đảo L&yacute; Sơn</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Du kh&aacute;ch c&oacute; thể thu&ecirc; nh&agrave; nghỉ hoặc kh&aacute;ch sạn tại L&yacute; Sơn với nhiều mức gi&aacute; kh&aacute;c nhau v&agrave; đừng qu&ecirc;n thỏa thuận trước khi đặt ph&ograve;ng nh&eacute; để đảm bảo dịch vụ v&agrave; gi&aacute; cả hợp l&yacute;.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Bạn c&oacute; thể tham khảo c&aacute;c tour du lịch, gi&aacute; t&agrave;u xe, đặt kh&aacute;ch sạn, m&aacute;y bay đi L&yacute; Sơn tại&nbsp;&nbsp;hoặc hotline: 1800 0076 để được tư vấn tất cả c&aacute;c loại dịch vụ khi du lịch L&yacute; Sơn để c&oacute; những sự lựa chọn ph&ugrave; hợp, mang lại cho chuyến đi hiệu quả tốt nhất.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Ch&uacute;c bạn c&oacute; chuyến đi L&yacute; Sơn&nbsp;vui vẻ c&ugrave;ng bạn b&egrave;, gia đ&igrave;nh.</span></p>', 2);
INSERT INTO `tin` (`id`, `tieuDe`, `title`, `description`, `keyword`, `img`, `date`, `noiDung`, `maLoaiTin`) VALUES
(22, 'Những địa điểm check in không thể bỏ qua khi đến Lý Sơn', 'Những địa điểm check in không thể bỏ qua khi đến Lý Sơn', 'Được biết đến là một trong những hòn đảo thiên đường ở Việt Nam, Lý Sơn Sở hữu rất nhiều địa điểm đẹp và hấp dẫn, đem đến cho bạn nguồn cảm hứng vô tận.', 'du lịch lý sơn , du lịch đà nẵng , thuê xe quảng ngãi,thuê xe đà nẵng', 'upload\\VDmw_bi-quyet-1.jpg', '2019-04-02', '<p><span style=\"color:#000000\">Được biết đến l&agrave; một trong những h&ograve;n đảo thi&ecirc;n đường ở Việt Nam, L&yacute; Sơn Sở hữu rất nhiều địa điểm đẹp v&agrave; hấp dẫn, đem đến cho bạn nguồn cảm hứng v&ocirc; tận. H&atilde;y c&ugrave;ng ch&uacute;ng t&ocirc;i điểm danh những điểm check-in nhất định phải đến khi du lịch L&yacute; Sơn nh&eacute;!</span></p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\"><strong>Những điểm đến kh&ocirc;ng thể bỏ qua tại L&yacute; Sơn</strong></span></h2>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Cổng T&ograve; V&ograve;</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Từ cầu cảng ch&iacute;nh đi v&agrave;o cổng ch&agrave;o&nbsp;<strong>L&yacute; Sơn</strong>, rẽ tr&aacute;i đi men theo con đường nhỏ đến gần ch&ugrave;a Đục, bạn sẽ thấy một mỏm đ&aacute; nhỏ nằm s&aacute;t dưới biển. C&aacute;c bạn c&oacute; thể đến đ&acirc;y để đ&oacute;n những khoảnh khắc khi b&igrave;nh minh l&ecirc;n hoặc khi ho&agrave;ng h&ocirc;n dần bu&ocirc;ng xuống v&ocirc; c&ugrave;ng đẹp.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news/4.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>B&igrave;nh minh l&ecirc;n tại cổng T&ograve; V&ograve;- L&yacute; Sơn</em></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Đặc biệt l&agrave; l&uacute;c ho&agrave;ng h&ocirc;n bu&ocirc;ng xuống, tia nắng đa sắc m&agrave;u cuối ng&agrave;y chiếu v&agrave;o khối đ&aacute;, hướng &aacute;nh nh&igrave;n ra biển xanh, với v&ocirc; v&agrave;n b&atilde;i đ&aacute; nhỏ nằm rải r&aacute;c xung quanh, khiến cho khung cảnh nơi đ&acirc;y b&igrave;nh y&ecirc;n đến lạ thường.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news/5.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Ho&agrave;ng h&ocirc;n bu&ocirc;ng xuống ở cổng T&ograve; V&ograve;</em></span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Hang C&acirc;u</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Hang C&acirc;u cũng l&agrave; điểm đến th&iacute;ch hợp cho việc ngắm ho&agrave;ng h&ocirc;n ở&nbsp;<strong>L&yacute; Sơn</strong>. Nằm ở th&ocirc;n Đ&ocirc;ng, x&atilde; An Hải dưới ch&acirc;n n&uacute;i Thới Lới, Hang C&acirc;u c&oacute; một khung cảnh thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ giữa một b&ecirc;n l&agrave; biển, một b&ecirc;n l&agrave; n&uacute;i. Hang C&acirc;u được s&oacute;ng v&agrave; gi&oacute; biển b&agrave;o m&ograve;n, kho&eacute;t s&acirc;u v&agrave;o l&ograve;ng n&uacute;i v&agrave; h&igrave;nh th&agrave;nh c&aacute;ch nay h&agrave;ng ng&agrave;n năm từ nham thạch. Khung cảnh nơi đ&acirc;y mang một vẻ đẹp hoang sơ, rất thơ mộng, h&uacute;t hồn kh&aacute;ch du lịch.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news/6.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Hang C&acirc;u l&uacute;c chiều t&agrave;</em></span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Đảo B&eacute;</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Đảo B&eacute; c&ograve;n c&oacute; t&ecirc;n gọi kh&aacute;c l&agrave; Đảo B&igrave;nh An, đ&uacute;ng như t&ecirc;n gọi của n&oacute; vậy. Đảo c&oacute; diện t&iacute;ch rất b&eacute; v&agrave; tr&ocirc;ng v&ocirc; c&ugrave;ng y&ecirc;n b&igrave;nh. L&agrave; một trong 3 h&ograve;n đảo thuộc đảo&nbsp;<strong>L&yacute; Sơn</strong>, c&aacute;ch đảo lớn L&yacute; Sơn 3 hải l&yacute; về ph&iacute;a T&acirc;y Bắc. Đảo B&eacute; tuy c&oacute; diện t&iacute;ch nhỏ nhưng lại c&oacute; một b&atilde;i tắm đẹp tuyệt vời với l&agrave;n c&aacute;t trắng mịn, bao bọc bởi c&aacute;nh cung v&aacute;ch đ&aacute; cao v&agrave; những con s&oacute;ng tung bọt trắng x&oacute;a ng&agrave;y đ&ecirc;m.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news/7.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Đảo B&eacute;- Đảo B&igrave;nh An</em></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Đảo b&eacute; đẹp ở nước biển xanh trong với gam m&agrave;u xanh lam, xanh ngọc pha trộn h&ograve;a quyện, bởi những mỏm đ&aacute; trầm t&iacute;ch n&uacute;i lửa h&agrave;ng triệu năm tạo th&agrave;nh h&igrave;nh v&ograve;ng cung &ocirc;m lấy biển.</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Cột cờ L&yacute; Sơn</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">C&ocirc;ng tr&igrave;nh Cột cờ Tổ quốc tại huyện<strong>&nbsp;Đảo L&yacute; Sơn</strong>&nbsp;được khởi c&ocirc;ng x&acirc;y dựng ng&agrave;y 4/5/2013, tr&ecirc;n ngọn n&uacute;i Thới Lới- ngọn n&uacute;i cao nhất L&yacute; Sơn. Với chiều cao 20m, mặt hướng ra quần đảo Ho&agrave;ng Sa, cột mốc khẳng định chủ quyền l&atilde;nh thổ biển đảo Việt Nam.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Đ&acirc;y l&agrave; địa điểm được nhiều du kh&aacute;ch ưa th&iacute;ch check-in, đặc biệt l&agrave; những bạn trẻ. Đến với cột cờ l&yacute; Sơn niềm tự h&agrave;o d&acirc;n tộc, &yacute; thức tr&aacute;ch nhiệm của bản th&acirc;n như được trỗi dậy mạnh mẽ hơn bao giờ hết.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news/8.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>Cột cờ L&yacute; Sơn</em></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">C&ograve;n rất nhiều địa điểm hấp dẫn nơi đ&acirc;y như Ch&ugrave;a Hang, Bờ K&egrave;, C&aacute;nh đồng tỏi, Ch&ugrave;a Đục- Quan &Acirc;m Đ&agrave;i, Hồ nước ngọt Thới Lới,.. chần chừ g&igrave; nữa m&agrave; kh&ocirc;ng rủ bạn b&egrave;, x&aacute;ch ba l&ocirc; l&ecirc;n v&agrave; đi ngay th&ocirc;i n&agrave;o.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Để biết th&ecirc;m th&ocirc;ng tin chi tiết về c&aacute;c chuyến đi ra L&yacute; Sơn c&aacute;c tour du lịch ở l&yacute; sơn,... h&atilde;y li&ecirc;n hệ hotline&nbsp;<strong>1800 0076</strong>&nbsp;để được<strong>&nbsp;Vinass&nbsp;</strong>tư vấn c&aacute;c dịch vụ cụ thể v&agrave; nhanh nhất.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Ch&uacute;c bạn c&oacute; thời gian vui vẻ, thoải m&aacute;i tại L&yacute; Sơn!</span></p>', 1),
(23, 'Những lưu ý khi đi tàu ra đảo Lý Sơn bạn cần biết', 'Những lưu ý khi đi tàu ra đảo Lý Sơn bạn cần biết', 'Hành trình đi tàu ra Đảo Lý Sơn vô cùng thú vị, bạn sẽ được ngắm nhìn biển xanh, mây trắng, gió biển và đàn hải âu bay rợp trời. Nếu may mắn đi trúng dịp bạn có thể thưởng thức màn bay vô cùng đẹp mắt của những chú cá chuồn.', 'du lịch lý sơn , du lịch đà nẵng , thuê xe quảng ngãi,thuê xe đà nẵng', 'upload\\bjMb_dich-vu-du-lich-ly-son-1.jpg', '2019-04-02', '<p><span style=\"color:#000000\"><strong>H&agrave;nh tr&igrave;nh đi t&agrave;u ra Đảo L&yacute; Sơn</strong>&nbsp;v&ocirc; c&ugrave;ng th&uacute; vị, bạn sẽ được ngắm nh&igrave;n biển xanh, m&acirc;y trắng, gi&oacute; biển v&agrave; đ&agrave;n hải &acirc;u bay rợp trời. Nếu may mắn đi tr&uacute;ng dịp bạn c&oacute; thể thưởng thức m&agrave;n bay v&ocirc; c&ugrave;ng đẹp mắt của những ch&uacute; c&aacute; chuồn. Tuy nhi&ecirc;n, bạn cần nắm một số th&ocirc;ng tin để đi t&agrave;u ra Đảo L&yacute; Sơn một c&aacute;ch an to&agrave;n v&agrave; đ&uacute;ng lịch tr&igrave;nh.</span></p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\"><strong>Những điều cần lưu &yacute;</strong></span></h2>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\">Lựa chọn giờ t&agrave;u đi L&yacute; Sơn thật hợp l&yacute;</span></h2>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Thủ tục đi<strong>&nbsp;t&agrave;u cao tốc ở L&yacute; Sơn</strong>&nbsp;cũng kh&ocirc;ng qu&aacute; ph&uacute;c tạp như những chuyến bay, nhưng bạn vẫn n&ecirc;n canh dư từ 30 đến 60 ph&uacute;t để đảm bảo đ&uacute;ng giờ v&eacute; t&agrave;u đi L&yacute; Sơn. Khi đến cảng, nếu bạn giải quyết nhanh thủ tục chỉ tốn 5-10 ph&uacute;t để lấy v&eacute; xuống t&agrave;u.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news/1.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>H&agrave;nh kh&aacute;ch xếp h&agrave;ng chuẩn bị l&ecirc;n t&agrave;u</em></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Ph&ograve;ng v&eacute; được mở cửa v&agrave;o 6h30 h&agrave;ng ng&agrave;y, mỗi người xếp h&agrave;ng sẽ được mua 2 v&eacute;. Khi mua v&eacute;, bạn sẽ khai b&aacute;o họ t&ecirc;n, năm sinh, địa chỉ, số CMND. Đối với c&aacute;c bạn đi đo&agrave;n đ&ocirc;ng c&oacute; thể đăng k&yacute; trước danh s&aacute;ch của đo&agrave;n ( bao gồm đầy đủ th&ocirc;ng tin như tr&ecirc;n) k&egrave;m theo số điện thoại đại diện của trưởng đo&agrave;n v&agrave; fax về cho ban quản l&yacute; cảng chậm nhất trước 15h30 của ng&agrave;y h&ocirc;m trước ng&agrave;y đi, đến khi mua v&eacute; cần mang theo danh s&aacute;ch gốc.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Một số khung giờ t&agrave;u thường chạy: 7h30,8h30, 9h30, 10h30, 11h30, 13h30, 14h30, 15h30.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Một số khung giờ t&agrave;u chạy (nhưng &iacute;t khi c&oacute; chuyến): 8h00,10h00,12h00,12h30,14h00</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">Đối với kh&aacute;ch du lịch bị say xe</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">C&aacute;c du kh&aacute;ch bị say t&agrave;u xe cần phải uống thuốc chống say t&agrave;u trước 1 giờ l&ecirc;n t&agrave;u. Cảm gi&aacute;c say t&agrave;u khi đi ra Đảo L&yacute; Sơn mệt hơn cảm gi&aacute;c say xe rất nhiều. Khi say xe, bạn chỉ bị giật tới, giật lui; c&ograve;n khi đi t&agrave;u bạn bị giật tứ ph&iacute;a. V&igrave; vậy bạn cần chọn v&eacute; t&agrave;u ph&ugrave; hợp cho kh&aacute;ch say xe:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\">Chọn t&agrave;u si&ecirc;u tốc đi 30 ph&uacute;t để nhanh đến Đảo</span></li>\r\n	<li><span style=\"color:#000000\">Chọn t&agrave;u to để &iacute;t lắc lư hơn khi chạy</span></li>\r\n	<li><span style=\"color:#000000\">Chọn t&agrave;u c&oacute; điều h&ograve;a v&agrave; kh&ocirc;ng gian tho&aacute;ng m&aacute;t</span></li>\r\n	<li><span style=\"color:#000000\">N&ecirc;n lựa chọn c&aacute;c v&eacute; t&agrave;u c&oacute; số thứ tự nhỏ hơn 30 để c&oacute; thể ngồi dưới đu&ocirc;i t&agrave;u, đỡ say.</span></li>\r\n</ul>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Ngo&agrave;i ra, c&aacute;c du kh&aacute;ch say t&agrave;u n&ecirc;n ngồi im một chỗ tr&ecirc;n ghế đi t&agrave;u. Kh&ocirc;ng n&ecirc;n l&ecirc;n boong t&agrave;u ngắm cảnh v&agrave; khi xuống t&agrave;u phải đi từ từ để tr&aacute;nh trường hợp bị say đất.</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">N&ecirc;n mặc g&igrave; khi đi t&agrave;u ra<strong>&nbsp;Đảo L&yacute; Sơn</strong></span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Gi&oacute; v&agrave; nắng phần phật khi t&agrave;u chạy tốc độ cao đối với c&aacute;c du kh&aacute;ch đi tr&ecirc;n boong t&agrave;u. V&igrave; thế bạn cần chọn những bộ trang phục c&oacute; khả năng chống nắng, c&ograve;n nếu kh&ocirc;ng bạn phải chuẩn bị kem chống nắng nếu kh&ocirc;ng muốn bị nướng giữa biển. C&oacute; th&ecirc;m một đ&ocirc;i k&iacute;nh chống nắng nữa th&igrave; c&ograve;n g&igrave; bằng, bạn c&oacute; thể thỏa sức check-in những tấm h&igrave;nh đẹp. V&agrave; đặc biệt bạn n&ecirc;n mang gi&agrave;y khi đi t&agrave;u để tr&aacute;nh c&aacute;c tai nạn kh&ocirc;ng mong muốn xảy ra.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news/2.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><span style=\"color:#000000\"><em>H&igrave;nh ảnh kh&aacute;ch du lịch check-in khi đi t&agrave;u ra L&yacute; Sơn</em></span></p>\r\n\r\n<h2 dir=\"ltr\"><span style=\"color:#000000\"><strong>Những điều th&uacute; vị khi đi t&agrave;u ra L&yacute; Sơn</strong></span></h2>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\">L&ecirc;n boong t&agrave;u ngắm cảnh</span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Khi t&agrave;u bắt đầu khởi h&agrave;nh, đối với c&aacute;c h&agrave;nh kh&aacute;ch kh&ocirc;ng bị say t&agrave;u xe th&igrave; đ&acirc;y l&agrave; khoảng thời gian đầy th&uacute; vị. Bạn c&oacute; thể l&ecirc;n boong t&agrave;u để tận hưởng kh&ocirc;ng gian m&aacute;t mẻ, cảm gi&aacute;c khoan kho&aacute;i, dễ chịu. Bạn sẽ cảm nhận được sự m&ecirc;nh m&ocirc;ng của biển cả, nước biển xanh biếc, khung cảnh ng&uacute;t ng&agrave;n kh&ocirc;ng thấy bờ bến.</span></p>\r\n\r\n<p dir=\"ltr\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news/3.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Tận hưởng kh&ocirc;ng kh&iacute; m&aacute;t lạnh tr&ecirc;n boong t&agrave;u</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Bạn c&ograve;n được ngắm nh&igrave;n những đ&agrave;n hải &acirc;u bay rợp tr&ecirc;n mặt biển, lượn lờ ng&acirc;y b&ecirc;n cạnh th&igrave; c&ograve;n g&igrave; th&uacute; vị hơn. Những trải nghiệm kh&ocirc;ng phải đi đ&acirc;u cũng gặp.</span></p>\r\n\r\n<h3 dir=\"ltr\"><span style=\"color:#000000\"><u><strong>Lưu &yacute;:</strong></u></span></h3>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Để chuyến đi của bạn kh&ocirc;ng c&ograve;n g&igrave; trở ngại bạn cần nắm chắc c&aacute;c khoảng thời gian di chuyển xe từ c&aacute;c địa điểm đến cảng Sa Kỳ sau:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\">Di chuyển từ s&acirc;n bay chu Lai đến Cảng Sa Kỳ khoảng 50 ph&uacute;t.</span></li>\r\n	<li><span style=\"color:#000000\">Từ Ga Quảng Ng&atilde;i đến Cảng Sa Kỳ khoảng 25 ph&uacute;t.</span></li>\r\n	<li><span style=\"color:#000000\">Từ Đ&agrave; Nẵng đi Sa Kỳ khoảng 160 ph&uacute;t.</span></li>\r\n	<li><span style=\"color:#000000\">Xe hội an đi Sa Kỳ khoảng 140 ph&uacute;t.</span></li>\r\n</ul>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Để bắt xe đi L&yacute; Sơn an to&agrave;n v&agrave; nhanh nhất h&atilde;y li&ecirc;n hệ hotline 1800 0079 để được cung cấp những th&ocirc;ng tin đầy đủ nhất, những d&ograve;ng xe mới nhất, kh&ocirc;ng hư hỏng, gi&aacute; rẻ của VINASS. Ngo&agrave;i ra ch&uacute;ng t&ocirc;i c&ograve;n cung cấp c&aacute;c tour du lịch L&yacute; Sơn v&ocirc; c&ugrave;ng hấp dẫn.</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"color:#000000\">Ch&uacute;c bạn c&oacute; khoảng thời gian thư gi&atilde;n thật thoải m&aacute;i tại Đảo L&yacute; Sơn nh&eacute;!</span></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\">&nbsp;</p>', 1),
(24, 'Ngày tết ở Lý Sơn có gì vui?', 'Ngày tết ở Lý Sơn có gì vui?', 'Những ngày cuối năm, dẫu công việc tất bật, nhưng đâu đó trong sâu thẳm tấm lòng của mỗi người đều chứa đựng sự lắng đọng của tâm hồn. Đó là ký ức của ngày Tết, là niềm mong nỗi nhớ mau chóng về với quê hương, cội nguồn.', 'du lịch lý sơn , du lịch đà nẵng , thuê xe quảng ngãi,thuê xe đà nẵng', 'upload\\XCao_cho-tet-3.jpg', '2019-04-03', '<p><span style=\"color:#000000\">Những ng&agrave;y cuối năm, dẫu c&ocirc;ng việc tất bật, nhưng đ&acirc;u đ&oacute; trong s&acirc;u thẳm tấm l&ograve;ng của mỗi người đều chứa đựng sự lắng đọng của t&acirc;m hồn. Đ&oacute; l&agrave; k&yacute; ức của ng&agrave;y Tết, l&agrave; niềm mong nỗi nhớ mau ch&oacute;ng về với qu&ecirc; hương, cội nguồn. Đ&oacute; cũng l&agrave; c&acirc;u chuyện &yacute; nghĩa về gi&aacute; trị văn h&oacute;a truyền thống trong ng&agrave;y Tết ở đảo L&yacute; Sơn m&agrave; t&ocirc;i c&oacute; dịp được nghe kể.</span></p>\r\n\r\n<h2><span style=\"color:#000000\"><strong>Trở về với k&yacute; ức</strong></span></h2>\r\n\r\n<p><span style=\"color:#000000\">Với ai cũng thế, ng&agrave;y Tết đ&atilde; đi v&agrave;o k&yacute; ức tuổi thơ với sự h&aacute;o hức, chờ mong được x&uacute;ng x&iacute;nh trong bộ quần &aacute;o mới, được nhận phong b&igrave; l&igrave; x&igrave;&hellip; Trẻ con ở L&yacute; Sơn cũng vậy. Anh Phan Đ&igrave;nh Độ, người con của đất đảo L&yacute; Sơn, hiện l&agrave; Trưởng ph&ograve;ng Quản l&yacute; di sản văn h&oacute;a (Sở VH-TT&amp;DL) t&acirc;m t&igrave;nh rằng: Nh&agrave; c&oacute; 7 anh em, khi bố c&ograve;n sống, năm n&agrave;o cũng vậy, trước l&uacute;c giao thừa, &ocirc;ng đ&aacute;nh thức tất cả c&aacute;c con dậy, mặc quần &aacute;o mới, rồi cả nh&agrave; qu&acirc;y quần b&ecirc;n nhau đ&oacute;n thời khắc chuyển giao giữa năm cũ v&agrave; năm mới. Tất cả đều nguyện cầu một năm mới an l&agrave;nh, gặp nhiều may mắn.</span></p>\r\n\r\n<p><span style=\"color:#000000\">Đ&oacute; l&agrave; k&yacute; ức kh&ocirc;ng thể phai mờ. V&agrave; như đ&atilde; th&agrave;nh lệ, d&ugrave; đ&atilde; c&oacute; gia đ&igrave;nh ri&ecirc;ng v&agrave; c&oacute; nh&agrave; ở th&agrave;nh phố, nhưng năm n&agrave;o gia đ&igrave;nh của cả 7 anh em đều về L&yacute; Sơn đ&oacute;n Tết.</span></p>\r\n\r\n<p><span style=\"color:#000000\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news/29.jpg\" style=\"height:99%; width:99%\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#000000\"><em>&nbsp;C&aacute;c bậc cao ni&ecirc;n c&uacute;ng tế thần linh trước khi tổ chức lễ hội đua thuyền</em></span></p>\r\n\r\n<p><span style=\"color:#000000\">Anh Độ cho biết, kh&ocirc;ng kh&iacute; ng&agrave;y Tết ở L&yacute; Sơn lắng đọng với những gi&aacute; trị văn h&oacute;a truyền thống đặc trưng. Mỗi người cảm nhận kh&aacute;c nhau về ng&agrave;y Tết, với anh, Tết ở L&yacute; Sơn đặc biệt hơn cả l&agrave; t&iacute;nh cố kết cộng đồng th&ocirc;ng qua những phong tục, tập qu&aacute;n từ rất l&acirc;u đời. V&agrave; ng&agrave;y Tết ở L&yacute; Sơn cũng kh&ocirc;ng sao qu&ecirc;n được hương vị của những m&oacute;n ăn truyền thống.</span></p>\r\n\r\n<p><span style=\"color:#000000\">Nhiều người đ&atilde; c&oacute; dịp thưởng thức b&aacute;nh &iacute;t l&aacute; gai ở L&yacute; Sơn với hương vị thơm ngon rất đặc trưng m&agrave; hiếm nơi n&agrave;o c&oacute; được. Song c&oacute; lẽ, kh&aacute;ch thập phương &iacute;t ai biết được rằng, b&aacute;nh &iacute;t l&agrave; m&oacute;n kh&ocirc;ng thể thiếu trong m&acirc;m c&uacute;ng gia ti&ecirc;n của người d&acirc;n đất đảo trong ng&agrave;y Tết. Ng&agrave;y nay, c&oacute; rất nhiều loại b&aacute;nh được sản xuất theo c&ocirc;ng nghệ hiện đại, nhưng với người d&acirc;n L&yacute; Sơn kh&ocirc;ng g&igrave; c&oacute; thể thay thế b&aacute;nh &iacute;t l&aacute; gai tr&ecirc;n b&agrave;n, bởi n&eacute;t văn h&oacute;a truyền thống đ&atilde; ăn s&acirc;u v&agrave;o t&acirc;m thức.</span></p>\r\n\r\n<p><span style=\"color:#000000\">Một điều đặc biệt nữa l&agrave;, ng&agrave;y Tết c&oacute; rất nhiều m&oacute;n ăn, nhưng với nhiều gia đ&igrave;nh ở L&yacute; Sơn, đặc biệt hơn cả vẫn l&agrave; m&oacute;n c&aacute; đ&ograve;n kho nước dừa, hương vị thơm ngon đến độ từ tấm b&eacute; cho đến l&uacute;c về gi&agrave;, d&ugrave; đi khắp c&aacute;c phương trời, nhiều người qu&ecirc; L&yacute; Sơn vẫn nhớ m&oacute;n c&aacute; đ&ograve;n trong ng&agrave;y Tết.</span></p>\r\n\r\n<h2><span style=\"color:#000000\"><strong>Độc đ&aacute;o c&aacute;c nghi lễ truyền thống</strong></span></h2>\r\n\r\n<p><span style=\"color:#000000\">Theo c&aacute;c bậc cao ni&ecirc;n ở L&yacute; Sơn, từ xa xưa cho đến ng&agrave;y nay, v&agrave;o dịp Tết cổ truyền, tr&ecirc;n đất đảo vẫn giữ g&igrave;n nguy&ecirc;n vẹn c&aacute;c nghi lễ truyền thống, phong tục Tết của người Việt vẫn rất đậm n&eacute;t. Từ việc đi tảo mộ, trang ho&agrave;ng b&agrave;n thờ gia ti&ecirc;n, mua sắm c&aacute;c lễ vật để c&uacute;ng tế ở c&aacute;c đ&igrave;nh, miếu&hellip; đều được chuẩn bị hết sức chu đ&aacute;o. Ng&agrave;y 24 th&aacute;ng Chạp, tất cả c&aacute;c đ&igrave;nh, ch&ugrave;a tr&ecirc;n đảo đều thực hiện nghi thức trồng đu l&ecirc;n phướn. Đ&acirc;y l&agrave; n&eacute;t đẹp văn h&oacute;a truyền thống hết sức độc đ&aacute;o, l&agrave; nghi thức để rước thần linh.</span></p>\r\n\r\n<p><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news/30.jpg\" style=\"height:99%; width:99%\" /></p>\r\n\r\n<h3 style=\"text-align:center\"><span style=\"color:#000000\"><em>Lễ hội đua thuyền ở L&yacute; Sơn</em></span></h3>\r\n\r\n<p><span style=\"color:#000000\"><strong>Đặc biệt</strong>, v&agrave;o ng&agrave;y mồng 1 Tết, Ban tế tự của đ&igrave;nh mang quả b&aacute;nh, trầu rượu đến nh&agrave; thờ c&aacute;c vị tiền hiền của l&agrave;ng để ra mắt, nhằm b&agrave;y tỏ l&ograve;ng tri &acirc;n. Một nghi thức quan trọng nữa của l&agrave;ng l&agrave; lễ động thổ, diễn ra v&agrave;o tối mồng 2 Tết tại đ&igrave;nh l&agrave;ng. Lễ động thổ được tổ chức hết sức trang trọng, gắn liền với t&acirc;m thức của cư d&acirc;n n&ocirc;ng nghiệp. Theo quan niệm, từ ng&agrave;y 30 th&aacute;ng Chạp đến mồng 1 Tết, kh&ocirc;ng ai được l&agrave;m động đến đất đai, kể cả c&oacute; người chết cũng kh&ocirc;ng được mai t&aacute;ng v&agrave;o ng&agrave;y n&agrave;y, v&igrave; chưa được thần linh cho ph&eacute;p, m&agrave; phải đợi đến tối mồng 2 Tết sau nghi lễ động thổ.</span></p>\r\n\r\n<p><span style=\"color:#000000\">C&oacute; rất nhiều nghi lễ trong ng&agrave;y Tết ở L&yacute; Sơn li&ecirc;n quan đến đời sống sản xuất của cư d&acirc;n. Ng&agrave;y Tết, tất cả c&aacute;c chủ thuyền tr&ecirc;n đảo đều đến c&aacute;c dinh miếu trong l&agrave;ng để ra mắt thần, l&agrave;m lễ nguyện cầu năm tới l&agrave;m ăn được m&ugrave;a, sức khỏe dồi d&agrave;o. Đến tối mồng 3, s&aacute;ng mồng 4, tất cả dinh miếu c&oacute; thuyền đua, đặc biệt l&agrave; hai đ&igrave;nh l&agrave;ng An Vĩnh, An Hải l&agrave;m lễ ra tr&ograve;, tức tổ chức c&aacute;c tr&ograve; chơi.</span></p>\r\n\r\n<p><span style=\"color:#000000\">C&aacute;c tr&ograve; chơi được tổ chức kh&ocirc;ng đơn thuần l&agrave; phục vụ đời sống tinh thần của người d&acirc;n, m&agrave; c&ograve;n l&agrave; một tr&ograve; diễn trước thần linh. Đặc biệt, ng&agrave;y Tết ở L&yacute; Sơn kh&ocirc;ng thể thiếu lễ hội đua thuyền, từ gi&agrave; ch&iacute; trẻ đều mong đến ng&agrave;y được xem c&aacute;c tay đua trổ t&agrave;i. Đến ng&agrave;y mồng 7, c&aacute;c l&agrave;ng l&agrave;m lễ hạ n&ecirc;u, tế c&aacute;o thần linh kết th&uacute;c c&aacute;c nghi lễ mừng năm mới, để trở lại cuộc sống b&igrave;nh thường.</span></p>', 1);

