--
-- Cấu trúc bảng cho bảng `travel`
--

CREATE TABLE `travel` (
  `id` int(11) NOT NULL,
  `tenTour` varchar(500) COLLATE utf8mb4_unicode_ci NOT NULL,
  `title` varchar(400) COLLATE utf8mb4_unicode_ci NOT NULL,
  `tomTat` varchar(500) COLLATE utf8mb4_unicode_ci NOT NULL,
  `keyword` varchar(400) COLLATE utf8mb4_unicode_ci NOT NULL,
  `gia` int(11) NOT NULL,
  `img` varchar(500) COLLATE utf8mb4_unicode_ci NOT NULL,
  `date` date NOT NULL,
  `noiDung` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `khuyenMai` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `travel`
--

INSERT INTO `travel` (`id`, `tenTour`, `title`, `tomTat`, `keyword`, `gia`, `img`, `date`, `noiDung`, `khuyenMai`) VALUES
(3, 'Đà Nẵng - Lý Sơn', 'du lịch lý sơn , du lịch đà nẵng , thuê xe đà nẵng', 'Travel lý sơn chuyên tổ chức các tour du lịch lý sơn ,đà nẵng , huế và cho thuê xe du lịch', 'du lịch lý sơn , du lịch đà nẵng , thuê xe quảng ngãi', 2300000, 'upload/scZz_chualyson.jpg', '2019-03-28', '<p><span style=\"color:#000000\">Với Tour du lịch Đ&agrave; Nẵng&nbsp;- L&yacute; Sơn, ch&uacute;ng t&ocirc;i chuy&ecirc;n đưa đ&oacute;n kh&aacute;ch từ s&acirc;n bay Chu Lai du lịch L&yacute; Sơn. Lysonvn ch&uacute;ng t&ocirc;i lu&ocirc;n mang đến cho c&aacute;c bạn những tour du lịch chất lượng với gi&aacute; rẻ nhất.</span></p>\r\n\r\n<h3><span style=\"color:#000000\"><strong>Nội dung chuyến đi</strong></span></h3>\r\n\r\n<p><span style=\"color:#000000\">&nbsp;<strong>Ng&agrave;y 1:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\">Xe của ch&uacute;ng t&ocirc;i đ&oacute;n qu&yacute; kh&aacute;ch ở điểm hẹn trước.</span></li>\r\n	<li><span style=\"color:#000000\">Di chuyển đến cảng Sa Kỳ nhận v&eacute; ra đảo L&yacute; Sơn cho 2 người.</span></li>\r\n	<li><span style=\"color:#000000\">Sau khi ra đến đảo, nh&acirc;n vi&ecirc;n xe điện sẽ đến đ&oacute;n qu&yacute; kh&aacute;ch v&agrave; l&agrave;m thủ tục check-in cho qu&yacute; kh&aacute;ch tại kh&aacute;ch sạn Mường Thanh.</span></li>\r\n	<li><span style=\"color:#000000\">Du lịch, ăn uống tự do kh&ocirc;ng theo đo&agrave;n.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"color:#000000\"><strong>Ng&agrave;y 2:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\">Ăn s&aacute;ng tại kh&aacute;ch sạn Mường Thanh</span></li>\r\n	<li><span style=\"color:#000000\">Qu&yacute; kh&aacute;ch sẽ được nh&acirc;n vi&ecirc;n xe điện đ&oacute;n v&agrave; nhận 2 v&eacute; ra đảo b&eacute; v&agrave; quay về.(thời gian t&ugrave;y chọn).</span></li>\r\n	<li><span style=\"color:#000000\">Du lịch đảo b&eacute;, ăn uống, ẩm thực...</span></li>\r\n</ul>\r\n\r\n<p><span style=\"color:#000000\"><strong>Ng&agrave;y 3:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\">Ăn s&aacute;ng tại kh&aacute;ch sạn Mường Thanh</span></li>\r\n	<li><span style=\"color:#000000\">Di chuyển ra cảng về quay về.</span></li>\r\n	<li><span style=\"color:#000000\">Nh&acirc;n vi&ecirc;n của ch&uacute;ng t&ocirc;i sẽ đ&oacute;n qu&yacute; kh&aacute;ch v&agrave; di chuyển về Chu Lai.(Hoặc Hội An, Đ&agrave; Nẵng...)</span></li>\r\n	<li>&nbsp;</li>\r\n	<li style=\"text-align:center\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/news/4.jpg\" style=\"height:99%; width:99%\" />Tour Du lịch Chu Lai L&yacute; Sơn 3 ng&agrave;y 2 đ&ecirc;m</li>\r\n	<li>\r\n	<h2 style=\"text-align:left\"><span style=\"color:#000000\"><span style=\"font-size:30px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Giới thiệu địa điểm check-in tại L&yacute; Sơn</span></span></span></span></h2>\r\n\r\n	<div class=\"content\" style=\"-webkit-text-stroke-width:0px; text-align:left; text-indent:0px\">\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">L&yacute; Sơn - Một huyện đảo trực thuộc tỉnh Quảng Ng&atilde;i - Được du kh&aacute;ch ưu &aacute;i v&iacute; von như đảo &ldquo;Jeju của Việt Nam&rdquo; bởi cảnh sắc đẹp y&ecirc;n b&igrave;nh, n&ecirc;n thơ. V&agrave; đặc biệt, đ&acirc;y cũng l&agrave; nơi cho ra đời những bức ảnh lung linh cực chất xuất hiện trong mỗi chuyến du h&iacute; nơi đ&acirc;y của c&aacute;c bạn trẻ tr&ecirc;n khắp mạng x&atilde; hội. Chỉ với một thao t&aacute;c đơn giản - T&igrave;m kiếm h&igrave;nh ảnh theo hastag #lyson, bạn đ&atilde; c&oacute; thể chi&ecirc;m ngưỡng h&agrave;ng ng&agrave;n bức ảnh đẹp được check-in tại &ldquo;vương quốc tỏi&rdquo; với những g&oacute;c chụp lung linh, kh&ocirc;ng ch&ecirc; v&agrave;o đ&acirc;u được. V&agrave; dưới đ&acirc;y l&agrave; c&aacute;c điểm check-in đắt gi&aacute; nhất được Lysontrip chọn lựa v&agrave; gợi &yacute; cho bạn cho chuyến du lịch của c&aacute;c bạn. H&atilde;y lưu lại nh&eacute;</span></span></span></span></p>\r\n\r\n	<div class=\"row\" style=\"margin-left:-15px; margin-right:-15px\">\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"huyen dao ly son tu tren cao\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/du-lich-dao-ly-son-quang-ngai-lysontrip-1.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">01 Huyện đảo L&yacute; Sơn từ tr&ecirc;n cao</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"cong to vo ly son trip\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/du-lich-dao-ly-son-quang-ngai-lysontrip-2.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">02 Cổng T&ograve; V&ograve; trong &aacute;nh ho&agrave;ng h&ocirc;n</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"dinh thoi loi ly son trip\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/du-lich-dao-ly-son-quang-ngai-lysontrip-3.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">03 to&agrave;n cảnh L&yacute; Sơn từ Đinh Thới Lới</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"canh dong toi ly son trip\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/du-lich-dao-ly-son-quang-ngai-lysontrip-4.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">04 c&aacute;nh đ&ocirc;ng tỏi</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"hon mu cu ly son trip\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/du-lich-dao-ly-son-quang-ngai-lysontrip-5.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">05 H&ograve;n M&ugrave; Cu - ngọn hải đăng</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"dao be ly son trip\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/du-lich-dao-ly-son-quang-ngai-lysontrip-6.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">06 Đảo b&eacute;- đảo Ly Sơn</span></span></span></span></span></p>\r\n	</div>\r\n	</div>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Với vị tr&iacute; tuyệt đẹp &ldquo;tựa sơn, hướng hải&rdquo; - Ch&ugrave;a Đục tọa lạc ngay giữa sườn n&uacute;i Giếng Tiềng hướng thẳng ra biển lớn. Để đến được Ch&ugrave;a Đục, du kh&aacute;ch phải vượt qua hơn 100 bậc thang men theo triền n&uacute;i. Đường đi tuy c&oacute; hơi vất vả nhưng khi đ&atilde; đến được ch&ugrave;a, cảm nhận được &ldquo;vị&rdquo; thanh tịnh của nơi cửa Phật, t&igrave;m thấy t&acirc;m hồn tự do khi ph&oacute;ng tầm mắt nh&igrave;n ra biển, du kh&aacute;ch sẽ qu&ecirc;n ngay mệt mỏi v&agrave; cũng kh&ocirc;ng qu&ecirc;n t&igrave;m những g&oacute;c chụp đắt gi&aacute; cho album ảnh kỷ niệm L&yacute; Sơn của m&igrave;nh.</span></span></span></span></p>\r\n	<span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"chua duc son trip\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/du-lich-dao-ly-son-quang-ngai-lysontrip-7.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">07 Ch&ugrave;a Đục L&yacute; Sơn</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<h2 style=\"text-align:left\"><span style=\"color:#000000\"><span style=\"font-size:30px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Xe đưa đ&oacute;n tận nơi an to&agrave;n</span></span></span></span></h2>\r\n\r\n	<div class=\"content\" style=\"-webkit-text-stroke-width:0px; text-align:left; text-indent:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Với hệ thống hơn 30 xe từ 4 chỗ đến 45 chỗ chạy xuy&ecirc;n suốt tuyến Quảng Ng&atilde;i - Quảng Nam - Đ&agrave; Nẵng v&agrave; đội ngũ t&agrave;i xế nhiều năm kinh nghiệm, ch&uacute;ng t&ocirc;i sẽ gi&uacute;p qu&yacute; kh&aacute;ch c&oacute; những chuyến đi an to&agrave;n, vui vẻ v&agrave; c&oacute; những trải nghiệm du lịch th&uacute; vị. c&oacute;</span></span></span></span>\r\n\r\n	<div class=\"row\" style=\"margin-left:-15px; margin-right:-15px\">\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue xe da nang di cang sa ky ly son\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/xe-du-lich-ly-son-1.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">D&agrave;n xe mới, phong ph&uacute; v&agrave; đa dạng</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue xe da nang di cang sa ky ly son\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/xe-du-lich-ly-son-2.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Nội thất sang trọng tiện nghi du lịch</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue xe da nang di cang sa ky ly son\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/xe-du-lich-ly-son-3.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">T&agrave;i xế chu đ&aacute;o, chia sẻ kinh nghiệm du lịch</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue xe da nang di cang sa ky ly son\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/xe-du-lich-ly-son-4.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Xe 4-7-16-36 chỗ phục vụ mọi kh&aacute;ch du lịch</span></span></span></span></span></p>\r\n	</div>\r\n	</div>\r\n	</div>\r\n\r\n	<h2 style=\"text-align:left\"><span style=\"color:#000000\"><span style=\"font-size:30px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">T&agrave;u cao tốc đi cảng Sa Kỳ - L&yacute; Sơn</span></span></span></span></h2>\r\n\r\n	<div class=\"content\" style=\"-webkit-text-stroke-width:0px; text-align:left; text-indent:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">T&agrave;u Super Biển Đ&ocirc;ng l&agrave; 1 trong những đơn vị đồng h&agrave;nh với L&yacute; Sơn Trip. Với hệ thống t&agrave;u mới, hiện đại, chuyến đi của bạn từ đất liền ra L&yacute; Sơn v&agrave; ngược lại sẽ giảm được 1 phần thời gian. Với vận tốc 7 hải l&yacute;/giờ, t&agrave;u Super Biển Đ&ocirc;ng sẽ l&agrave; 1 bước tiến lớn cho du lịch L&yacute; Sơn.</span></span></span></span>\r\n\r\n	<div class=\"row\" style=\"margin-left:-15px; margin-right:-15px\">\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"tàu super biển đông đi lý sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/tau-khach-super-bien-dong-ly-son-1.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">T&agrave;u super biển đ&ocirc;ng đi l&yacute; sơn</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"nội thất tàu super biển đông đi lý sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/tau-khach-super-bien-dong-ly-son-2.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Nội thất t&agrave;u super biển đ&ocirc;ng đi l&yacute; sơn</span></span></span></span></span></p>\r\n	</div>\r\n	</div>\r\n	</div>\r\n\r\n	<h2 style=\"text-align:left\"><span style=\"color:#000000\"><span style=\"font-size:30px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Nghỉ ngơi tại kh&aacute;ch sạn Mường Thanh</span></span></span></span></h2>\r\n\r\n	<div class=\"content\" style=\"-webkit-text-stroke-width:0px; text-align:left; text-indent:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Kh&aacute;ch sạn Mường Thanh l&agrave; một trong những kh&aacute;ch sạn lớn nhất ở L&yacute; Sơn với đầy đủ tất cả c&aacute;c dịch vụ tiện nghi của một kh&aacute;ch sạn 4 sao. Tại đ&acirc;y bạn c&oacute; thể cảm nhận được sự y&ecirc;n tĩnh, sạch sẽ b&ecirc;n trong v&agrave; ngo&agrave;i kh&aacute;ch sạn. Ngo&agrave;i ra việc c&oacute; bể bơi v&agrave; c&oacute; được sự sạch sẽ của bể bơi cũng l&agrave; 1 điểm cộng lớn cho kh&aacute;ch sạn.</span></span></span></span>\r\n\r\n	<div class=\"row\" style=\"margin-left:-15px; margin-right:-15px\">\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue khách sạn du lịch Lý Sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/khach-san-muong-thanh-phong-1.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Tiện nghi kh&aacute;ch sạn mường thanh</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue khách sạn du lịch Lý Sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/khach-san-muong-thanh-phong-2.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Tiện nghi kh&aacute;ch sạn mường thanh</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue khách sạn du lịch Lý Sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/khach-san-muong-thanh-phong-3.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Tiện nghi kh&aacute;ch sạn mường thanh</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue khách sạn du lịch Lý Sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/khach-san-muong-thanh-phong-4.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Tiện nghi kh&aacute;ch sạn mường thanh</span></span></span></span></span></p>\r\n	</div>\r\n	</div>\r\n	</div>\r\n\r\n	<h2 style=\"text-align:left\"><span style=\"color:#000000\"><span style=\"font-size:30px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Nghỉ ngơi tại nh&agrave; nghỉ Quỳnh Anh</span></span></span></span></h2>\r\n\r\n	<div class=\"content\" style=\"-webkit-text-stroke-width:0px; text-align:left; text-indent:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Nh&agrave; nghỉ QUỲNH ANH nằm ở khu vực Cầu Cảng L&yacute; Sơn, đ&acirc;y l&agrave; địa điểm nhộn nhịp nhất gần trung t&acirc;m mua sắm, ăn uống, vui chơi giải tr&iacute;. Được đưa v&agrave;o hoạt động từ đầu năm 2016, với dịch vụ tốt, gi&aacute; cả hợp l&yacute; Nh&agrave; nghỉ QUỲNH ANH l&agrave; điểm dừng ch&acirc;n nghỉ ngơi, thưởng thức c&aacute;c m&oacute;n ăn đặc sản hấp dẫn nhất của du kh&aacute;ch tr&ecirc;n mọi miền đất nước khi đến tham quan nghỉ dưỡng v&agrave; c&ocirc;ng t&aacute;c tại Đảo L&yacute; Sơn xinh đẹp n&agrave;y. Đến với Nh&agrave; nghỉ QUỲNH ANH, Qu&yacute; kh&aacute;ch sẽ được đ&oacute;n tiếp nồng nhiệt, phục vụ chu đ&aacute;o, tận t&igrave;nh với th&aacute;i độ &acirc;n cần, niềm nở tạo cho du kh&aacute;ch đến đ&acirc;y cảm nhận được sự th&acirc;n thiện v&agrave; h&agrave;i l&ograve;ng. Chắc chắn rằng Qu&yacute; kh&aacute;ch sẽ c&oacute; cảm gi&aacute;c thoải m&aacute;i như quay về ch&iacute;nh ng&ocirc;i nh&agrave; của m&igrave;nh.</span></span></span></span>\r\n\r\n	<div class=\"row\" style=\"margin-left:-15px; margin-right:-15px\">\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue khách sạn du lịch Lý Sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/quynh-anh/1.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Tiện nghi nh&agrave; nghỉ Quỳnh Anh</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue khách sạn du lịch Lý Sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/quynh-anh/2.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Tiện nghi nh&agrave; nghỉ Quỳnh Anh</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue khách sạn du lịch Lý Sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/quynh-anh/3.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Tiện nghi nh&agrave; nghỉ Quỳnh Anh</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue khách sạn du lịch Lý Sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/quynh-anh/4.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Tiện nghi nh&agrave; nghỉ Quỳnh Anh</span></span></span></span></span></p>\r\n	</div>\r\n	</div>\r\n	</div>\r\n\r\n	<hr />\r\n	<div class=\"content\" style=\"-webkit-text-stroke-width:0px; text-align:left; text-indent:0px\">\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><strong>C&Ocirc;NG TY TNHH DU LỊCH LYSONVN</strong></span></span></span></span></p>\r\n\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><strong>Địa chỉ:</strong>&nbsp;Tầng 2, 121 C&ugrave; Ch&iacute;nh Lan, Thanh Kh&ecirc;, Đ&agrave; Nẵng</span></span></span></span></p>\r\n\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><strong>SĐT:</strong>&nbsp;0328811678</span></span></span></span></p>\r\n	</div>\r\n	</li>\r\n</ul>', 1),
(4, 'Hội An', 'du lịch lý sơn , du lịch đà nẵng , thuê xe đà nẵng', 'Travel lý sơn chuyên tổ chức các tour du lịch lý sơn ,đà nẵng , huế và cho thuê xe du lịch', 'du lịch lý sơn , du lịch đà nẵng , thuê xe quảng ngãi', 1500000, 'upload/x5bn_pho-co-hoi-an-lung-linh.jpg', '2019-03-28', '<p style=\"text-align:center\"><strong>Tour du lịch Đ&agrave; nẵng - Hội an&nbsp;2 ng&agrave;y 1 đ&ecirc;m&nbsp;của Travel L&yacute; Sơn với gi&aacute; rẻ&nbsp;</strong></p>\r\n\r\n<p><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/travel/anh-dep-du-lich-viet-nam-vietnam-travel-ha-long-bay-01.jpg\" style=\"height:99%; width:99%\" /></p>', 1);
INSERT INTO `travel` (`id`, `tenTour`, `title`, `tomTat`, `keyword`, `gia`, `img`, `date`, `noiDung`, `khuyenMai`) VALUES
(5, 'Chu Lai - Lý Sơn', 'du lịch lý sơn , du lịch đà nẵng , thuê xe đà nẵng', 'Travel lý sơn chuyên tổ chức các tour du lịch lý sơn ,đà nẵng , huế và cho thuê xe du lịch', 'du lịch lý sơn , du lịch đà nẵng , thuê xe quảng ngãi', 2600000, 'upload\\buYj_chulai-lyson.jpg', '2019-04-03', '<p><span style=\"color:#000000\">Với Tour du lịch Chu Lai - L&yacute; Sơn, ch&uacute;ng t&ocirc;i chuy&ecirc;n đưa đ&oacute;n kh&aacute;ch từ s&acirc;n bay Chu Lai du lịch L&yacute; Sơn. Lysonvn ch&uacute;ng t&ocirc;i lu&ocirc;n mang đến cho c&aacute;c bạn những tour du lịch chất lượng với gi&aacute; rẻ nhất.</span></p>\r\n\r\n<h3><strong><span style=\"color:#000000\">Nội dung chuyến đi</span></strong></h3>\r\n\r\n<p><span style=\"color:#000000\">&nbsp;<strong>Ng&agrave;y 1:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\">Xe của ch&uacute;ng t&ocirc;i đ&oacute;n qu&yacute; kh&aacute;ch ở điểm hẹn trước.</span></li>\r\n	<li><span style=\"color:#000000\">Di chuyển đến cảng Sa Kỳ nhận v&eacute; ra đảo L&yacute; Sơn cho 2 người.</span></li>\r\n	<li><span style=\"color:#000000\">Sau khi ra đến đảo, nh&acirc;n vi&ecirc;n xe điện sẽ đến đ&oacute;n qu&yacute; kh&aacute;ch v&agrave; l&agrave;m thủ tục check-in cho qu&yacute; kh&aacute;ch tại kh&aacute;ch sạn Mường Thanh.</span></li>\r\n	<li><span style=\"color:#000000\">Du lịch, ăn uống tự do kh&ocirc;ng theo đo&agrave;n.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"color:#000000\"><strong>Ng&agrave;y 2:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\">Ăn s&aacute;ng tại kh&aacute;ch sạn Mường Thanh</span></li>\r\n	<li><span style=\"color:#000000\">Qu&yacute; kh&aacute;ch sẽ được nh&acirc;n vi&ecirc;n xe điện đ&oacute;n v&agrave; nhận 2 v&eacute; ra đảo b&eacute; v&agrave; quay về.(thời gian t&ugrave;y chọn).</span></li>\r\n	<li><span style=\"color:#000000\">Du lịch đảo b&eacute;, ăn uống, ẩm thực...</span></li>\r\n</ul>\r\n\r\n<p><span style=\"color:#000000\"><strong>Ng&agrave;y 3:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\">Ăn s&aacute;ng tại kh&aacute;ch sạn Mường Thanh</span></li>\r\n	<li><span style=\"color:#000000\">Di chuyển ra cảng về quay về.</span></li>\r\n	<li><span style=\"color:#000000\">Nh&acirc;n vi&ecirc;n của ch&uacute;ng t&ocirc;i sẽ đ&oacute;n qu&yacute; kh&aacute;ch v&agrave; di chuyển về Chu Lai.(Hoặc Hội An, Đ&agrave; Nẵng...)</span></li>\r\n	<li>&nbsp;</li>\r\n	<li><span style=\"color:#000000\"><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/travel/dao-ly-son-1.jpg\" style=\"height:99%; width:99%\" /></span></li>\r\n	<li style=\"text-align:center\"><span style=\"color:#000000\">Tour Du lịch Chu Lai L&yacute; Sơn 3 ng&agrave;y 2 đ&ecirc;m</span></li>\r\n	<li><span style=\"color:#000000\">Trong thời gian du lịch tại đảo L&yacute; Sơn, qu&yacute; kh&aacute;ch được tự do di chuyển bằng xe điện v&agrave; kh&ocirc;ng mất ph&iacute; đi lại.</span></li>\r\n	<li><img alt=\"\" src=\"http://lysonvn.com/public/upload/files/travel/Capture.jpg\" style=\"height:99%; width:99%\" /></li>\r\n	<li>\r\n	<h2 style=\"text-align:left\"><span style=\"color:#000000\"><span style=\"font-size:30px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Giới thiệu địa điểm check-in tại L&yacute; Sơn</span></span></span></span></h2>\r\n\r\n	<div class=\"content\" style=\"-webkit-text-stroke-width:0px; text-align:left; text-indent:0px\">\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">L&yacute; Sơn - Một huyện đảo trực thuộc tỉnh Quảng Ng&atilde;i - Được du kh&aacute;ch ưu &aacute;i v&iacute; von như đảo &ldquo;Jeju của Việt Nam&rdquo; bởi cảnh sắc đẹp y&ecirc;n b&igrave;nh, n&ecirc;n thơ. V&agrave; đặc biệt, đ&acirc;y cũng l&agrave; nơi cho ra đời những bức ảnh lung linh cực chất xuất hiện trong mỗi chuyến du h&iacute; nơi đ&acirc;y của c&aacute;c bạn trẻ tr&ecirc;n khắp mạng x&atilde; hội. Chỉ với một thao t&aacute;c đơn giản - T&igrave;m kiếm h&igrave;nh ảnh theo hastag #lyson, bạn đ&atilde; c&oacute; thể chi&ecirc;m ngưỡng h&agrave;ng ng&agrave;n bức ảnh đẹp được check-in tại &ldquo;vương quốc tỏi&rdquo; với những g&oacute;c chụp lung linh, kh&ocirc;ng ch&ecirc; v&agrave;o đ&acirc;u được. V&agrave; dưới đ&acirc;y l&agrave; c&aacute;c điểm check-in đắt gi&aacute; nhất được Lysontrip chọn lựa v&agrave; gợi &yacute; cho bạn cho chuyến du lịch của c&aacute;c bạn. H&atilde;y lưu lại nh&eacute;</span></span></span></span></p>\r\n\r\n	<div class=\"row\" style=\"margin-left:-15px; margin-right:-15px\">\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"huyen dao ly son tu tren cao\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/du-lich-dao-ly-son-quang-ngai-lysontrip-1.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">01 Huyện đảo L&yacute; Sơn từ tr&ecirc;n cao</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"cong to vo ly son trip\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/du-lich-dao-ly-son-quang-ngai-lysontrip-2.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">02 Cổng T&ograve; V&ograve; trong &aacute;nh ho&agrave;ng h&ocirc;n</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"dinh thoi loi ly son trip\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/du-lich-dao-ly-son-quang-ngai-lysontrip-3.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">03 to&agrave;n cảnh L&yacute; Sơn từ Đinh Thới Lới</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"canh dong toi ly son trip\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/du-lich-dao-ly-son-quang-ngai-lysontrip-4.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">04 c&aacute;nh đ&ocirc;ng tỏi</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"hon mu cu ly son trip\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/du-lich-dao-ly-son-quang-ngai-lysontrip-5.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">05 H&ograve;n M&ugrave; Cu - ngọn hải đăng</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"dao be ly son trip\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/du-lich-dao-ly-son-quang-ngai-lysontrip-6.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">06 Đảo b&eacute;- đảo Ly Sơn</span></span></span></span></span></p>\r\n	</div>\r\n	</div>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Với vị tr&iacute; tuyệt đẹp &ldquo;tựa sơn, hướng hải&rdquo; - Ch&ugrave;a Đục tọa lạc ngay giữa sườn n&uacute;i Giếng Tiềng hướng thẳng ra biển lớn. Để đến được Ch&ugrave;a Đục, du kh&aacute;ch phải vượt qua hơn 100 bậc thang men theo triền n&uacute;i. Đường đi tuy c&oacute; hơi vất vả nhưng khi đ&atilde; đến được ch&ugrave;a, cảm nhận được &ldquo;vị&rdquo; thanh tịnh của nơi cửa Phật, t&igrave;m thấy t&acirc;m hồn tự do khi ph&oacute;ng tầm mắt nh&igrave;n ra biển, du kh&aacute;ch sẽ qu&ecirc;n ngay mệt mỏi v&agrave; cũng kh&ocirc;ng qu&ecirc;n t&igrave;m những g&oacute;c chụp đắt gi&aacute; cho album ảnh kỷ niệm L&yacute; Sơn của m&igrave;nh.</span></span></span></span></p>\r\n	<span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"chua duc son trip\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/du-lich-dao-ly-son-quang-ngai-lysontrip-7.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">07 Ch&ugrave;a Đục L&yacute; Sơn</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<h2 style=\"text-align:left\"><span style=\"color:#000000\"><span style=\"font-size:30px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Xe đưa đ&oacute;n tận nơi an to&agrave;n</span></span></span></span></h2>\r\n\r\n	<div class=\"content\" style=\"-webkit-text-stroke-width:0px; text-align:left; text-indent:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Với hệ thống hơn 30 xe từ 4 chỗ đến 45 chỗ chạy xuy&ecirc;n suốt tuyến Quảng Ng&atilde;i - Quảng Nam - Đ&agrave; Nẵng v&agrave; đội ngũ t&agrave;i xế nhiều năm kinh nghiệm, ch&uacute;ng t&ocirc;i sẽ gi&uacute;p qu&yacute; kh&aacute;ch c&oacute; những chuyến đi an to&agrave;n, vui vẻ v&agrave; c&oacute; những trải nghiệm du lịch th&uacute; vị. c&oacute;</span></span></span></span>\r\n\r\n	<div class=\"row\" style=\"margin-left:-15px; margin-right:-15px\">\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue xe da nang di cang sa ky ly son\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/xe-du-lich-ly-son-1.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">D&agrave;n xe mới, phong ph&uacute; v&agrave; đa dạng</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue xe da nang di cang sa ky ly son\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/xe-du-lich-ly-son-2.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Nội thất sang trọng tiện nghi du lịch</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue xe da nang di cang sa ky ly son\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/xe-du-lich-ly-son-3.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">T&agrave;i xế chu đ&aacute;o, chia sẻ kinh nghiệm du lịch</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue xe da nang di cang sa ky ly son\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/xe-du-lich-ly-son-4.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Xe 4-7-16-36 chỗ phục vụ mọi kh&aacute;ch du lịch</span></span></span></span></span></p>\r\n	</div>\r\n	</div>\r\n	</div>\r\n\r\n	<h2 style=\"text-align:left\"><span style=\"color:#000000\"><span style=\"font-size:30px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">T&agrave;u cao tốc đi cảng Sa Kỳ - L&yacute; Sơn</span></span></span></span></h2>\r\n\r\n	<div class=\"content\" style=\"-webkit-text-stroke-width:0px; text-align:left; text-indent:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">T&agrave;u Super Biển Đ&ocirc;ng l&agrave; 1 trong những đơn vị đồng h&agrave;nh với L&yacute; Sơn Trip. Với hệ thống t&agrave;u mới, hiện đại, chuyến đi của bạn từ đất liền ra L&yacute; Sơn v&agrave; ngược lại sẽ giảm được 1 phần thời gian. Với vận tốc 7 hải l&yacute;/giờ, t&agrave;u Super Biển Đ&ocirc;ng sẽ l&agrave; 1 bước tiến lớn cho du lịch L&yacute; Sơn.</span></span></span></span>\r\n\r\n	<div class=\"row\" style=\"margin-left:-15px; margin-right:-15px\">\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"tàu super biển đông đi lý sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/tau-khach-super-bien-dong-ly-son-1.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">T&agrave;u super biển đ&ocirc;ng đi l&yacute; sơn</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"nội thất tàu super biển đông đi lý sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/tau-khach-super-bien-dong-ly-son-2.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Nội thất t&agrave;u super biển đ&ocirc;ng đi l&yacute; sơn</span></span></span></span></span></p>\r\n	</div>\r\n	</div>\r\n	</div>\r\n\r\n	<h2 style=\"text-align:left\"><span style=\"color:#000000\"><span style=\"font-size:30px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Nghỉ ngơi tại kh&aacute;ch sạn Mường Thanh</span></span></span></span></h2>\r\n\r\n	<div class=\"content\" style=\"-webkit-text-stroke-width:0px; text-align:left; text-indent:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Kh&aacute;ch sạn Mường Thanh l&agrave; một trong những kh&aacute;ch sạn lớn nhất ở L&yacute; Sơn với đầy đủ tất cả c&aacute;c dịch vụ tiện nghi của một kh&aacute;ch sạn 4 sao. Tại đ&acirc;y bạn c&oacute; thể cảm nhận được sự y&ecirc;n tĩnh, sạch sẽ b&ecirc;n trong v&agrave; ngo&agrave;i kh&aacute;ch sạn. Ngo&agrave;i ra việc c&oacute; bể bơi v&agrave; c&oacute; được sự sạch sẽ của bể bơi cũng l&agrave; 1 điểm cộng lớn cho kh&aacute;ch sạn.</span></span></span></span>\r\n\r\n	<div class=\"row\" style=\"margin-left:-15px; margin-right:-15px\">\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue khách sạn du lịch Lý Sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/khach-san-muong-thanh-phong-1.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Tiện nghi kh&aacute;ch sạn mường thanh</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue khách sạn du lịch Lý Sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/khach-san-muong-thanh-phong-2.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Tiện nghi kh&aacute;ch sạn mường thanh</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue khách sạn du lịch Lý Sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/khach-san-muong-thanh-phong-3.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Tiện nghi kh&aacute;ch sạn mường thanh</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue khách sạn du lịch Lý Sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/khach-san-muong-thanh-phong-4.png\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Tiện nghi kh&aacute;ch sạn mường thanh</span></span></span></span></span></p>\r\n	</div>\r\n	</div>\r\n	</div>\r\n\r\n	<h2 style=\"text-align:left\"><span style=\"color:#000000\"><span style=\"font-size:30px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Nghỉ ngơi tại nh&agrave; nghỉ Quỳnh Anh</span></span></span></span></h2>\r\n\r\n	<div class=\"content\" style=\"-webkit-text-stroke-width:0px; text-align:left; text-indent:0px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\">Nh&agrave; nghỉ QUỲNH ANH nằm ở khu vực Cầu Cảng L&yacute; Sơn, đ&acirc;y l&agrave; địa điểm nhộn nhịp nhất gần trung t&acirc;m mua sắm, ăn uống, vui chơi giải tr&iacute;. Được đưa v&agrave;o hoạt động từ đầu năm 2016, với dịch vụ tốt, gi&aacute; cả hợp l&yacute; Nh&agrave; nghỉ QUỲNH ANH l&agrave; điểm dừng ch&acirc;n nghỉ ngơi, thưởng thức c&aacute;c m&oacute;n ăn đặc sản hấp dẫn nhất của du kh&aacute;ch tr&ecirc;n mọi miền đất nước khi đến tham quan nghỉ dưỡng v&agrave; c&ocirc;ng t&aacute;c tại Đảo L&yacute; Sơn xinh đẹp n&agrave;y. Đến với Nh&agrave; nghỉ QUỲNH ANH, Qu&yacute; kh&aacute;ch sẽ được đ&oacute;n tiếp nồng nhiệt, phục vụ chu đ&aacute;o, tận t&igrave;nh với th&aacute;i độ &acirc;n cần, niềm nở tạo cho du kh&aacute;ch đến đ&acirc;y cảm nhận được sự th&acirc;n thiện v&agrave; h&agrave;i l&ograve;ng. Chắc chắn rằng Qu&yacute; kh&aacute;ch sẽ c&oacute; cảm gi&aacute;c thoải m&aacute;i như quay về ch&iacute;nh ng&ocirc;i nh&agrave; của m&igrave;nh.</span></span></span></span>\r\n\r\n	<div class=\"row\" style=\"margin-left:-15px; margin-right:-15px\">\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue khách sạn du lịch Lý Sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/quynh-anh/1.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Tiện nghi nh&agrave; nghỉ Quỳnh Anh</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><img alt=\"thue khách sạn du lịch Lý Sơn\" class=\"img-fluid\" src=\"https://lysontrip.vn/templates/news/img/quynh-anh/2.jpg\" style=\"border-style:none; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle\" /></span></span></span></span>\r\n\r\n	<p style=\"text-align:center !important\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"background-color:#dddddd\">Tiện nghi nh&agrave; nghỉ Quỳnh Anh</span></span></span></span></span></p>\r\n	</div>\r\n\r\n	<div class=\"col-lg-6 col-md-8 offset-lg-0 offset-md-2 pt-2\" style=\"max-width:50%; padding-left:15px; padding-right:15px; padding-top:0.5rem !important; width:380px\"><span style=\"color:#000000\"><span style=\"font-size:16px\"><span style=\"font-f